import React, { useState, useEffect, useCallback, useMemo } from "react";
import axios from "axios";
import socket from "../socket";

import {
  BoltIcon,
  ClockIcon,
  UserIcon,
  ArrowRightIcon,
} from "@heroicons/react/24/outline";

import NavigationBar from "../components/navbar.jsx";

export default function TradePage() {
  const [myOffers, setMyOffers] = useState([]);
  const [marketOffers, setMarketOffers] = useState([]);
  const [newOffer, setNewOffer] = useState({
    offer_type: "sell",
    units: "",
    token_per_unit: "",
  });

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  const API = "http://localhost:5001";

  // -----------------------------------
  // Auth header
  // -----------------------------------
  const authHeader = () => {
    return {
      "Content-Type": "application/json",
      Authorization: `Bearer ${localStorage.getItem("token") || ""}`,
    };
  };

  // -----------------------------------
  // FETCH: My Offers + Market Offers
  // -----------------------------------
  const fetchOffers = useCallback(async () => {
    try {
      setLoading(true);

      // My Offers
      const mine = await axios.get(`${API}/api/offers/my-active`, {
        headers: authHeader(),
      });

      // Market offers
      const all = await axios.get(`${API}/api/offers/current`, {
        headers: authHeader(),
      });

      setMyOffers(mine.data.data || []);
      setMarketOffers(all.data.data || []);
      setError("");
    } catch (err) {
      console.error("Fetch Error:", err);
      const msg =
        err.response?.data?.msg ||
        err.response?.data?.message ||
        "Server error";
      setError(`Failed to load offers: ${msg}`);
    } finally {
      setLoading(false);
    }
  }, []);

  // -----------------------------------
  // SOCKET
  // -----------------------------------
  useEffect(() => {
    const token = localStorage.getItem("token");

    if (token) {
      socket.auth = { token };
      socket.connect();
    }

    fetchOffers();

    socket.on("offersUpdated", fetchOffers);

    return () => {
      socket.off("offersUpdated", fetchOffers);
      if (socket.connected) socket.disconnect();
    };
  }, [fetchOffers]);

  // -----------------------------------
  // CREATE OFFER
  // -----------------------------------
  const handleCreateOffer = async (e) => {
    e.preventDefault();

    const creator_id = localStorage.getItem("user_id");
    if (!creator_id) {
      setError("User ID missing. Log in again.");
      return;
    }

    if (!newOffer.units || !newOffer.token_per_unit) {
      setError("Units and token rate required.");
      return;
    }

    const body = {
      creator_id,
      offer_type: newOffer.offer_type,
      units: Number(newOffer.units),
      token_per_unit: Number(newOffer.token_per_unit),
    };

    try {
      await axios.post(`${API}/api/offers/create`, body, {
        headers: authHeader(),
      });

      setNewOffer({ offer_type: "sell", units: "", token_per_unit: "" });
      socket.emit("offerCreated");
      fetchOffers();
    } catch (err) {
      console.error("Create Error:", err);
      const msg =
        err.response?.data?.msg ||
        err.response?.data?.message ||
        "Could not create offer";
      setError(msg);
    }
  };

  const totalTokens =
    (Number(newOffer.units) || 0) * (Number(newOffer.token_per_unit) || 0);

  // -----------------------------------
  // CANCEL OFFER
  // -----------------------------------
  const handleCancelOffer = async (offerId) => {
    if (!window.confirm("Cancel this offer?")) return;

    try {
      await axios.post(
        `${API}/api/offers/cancel`,
        { offer_id: offerId, user_id: localStorage.getItem("user_id") },
        { headers: authHeader() }
      );

      socket.emit("offerCanceled");
      fetchOffers();
    } catch (err) {
      console.error("Cancel error:", err);
      alert("Cancel failed.");
    }
  };

  // -----------------------------------
  // ACCEPT NEGOTIATION
  // -----------------------------------
  const handleAcceptNegotiation = async (offerId) => {
    if (!window.confirm("Accept this negotiated price?")) return;

    try {
      await axios.post(
        `${API}/api/offers/accept-negotiation`,
        { offer_id: offerId, user_id: localStorage.getItem("user_id") },
        { headers: authHeader() }
      );

      socket.emit("offerUpdated");
      fetchOffers();
    } catch (err) {
      console.error("Accept Negotiation Error:", err);
      alert("Failed to accept negotiation.");
    }
  };

  // -----------------------------------
  // CANCEL NEGOTIATION
  // -----------------------------------
  const handleCancelNegotiation = async (offerId) => {
    if (!window.confirm("Cancel this negotiation?")) return;

    try {
      await axios.post(
        `${API}/api/offers/cancel-negotiation`,
        { offer_id: offerId, user_id: localStorage.getItem("user_id") },
        { headers: authHeader() }
      );

      socket.emit("offerUpdated");
      fetchOffers();
    } catch (err) {
      console.error("Cancel Negotiation Error:", err);
      alert("Failed to cancel negotiation.");
    }
  };

  // -----------------------------------
  // OFFER CARD
  // -----------------------------------
  const OfferCard = ({ offer, isOwnOffer }) => {
    const created = offer.created_at
      ? new Date(offer.created_at).toLocaleString()
      : "—";

    return (
      <div
        className={`p-5 rounded-xl shadow-lg transition ${
          offer.offer_type === "sell"
            ? "bg-blue-800/70 border-l-4 border-blue-300"
            : "bg-red-800/70 border-l-4 border-red-300"
        }`}
      >
        <div className="flex justify-between mb-3 pb-2 border-b border-white/20">
          <h3 className="font-bold text-lg flex items-center gap-2">
            <ArrowRightIcon className="w-5 h-5 text-yellow-400" />
            {offer.offer_type.toUpperCase()} OFFER
          </h3>
          <span className="px-3 py-1 text-xs bg-green-500 rounded-full font-bold shadow-md">
            {offer.status.toUpperCase()}
          </span>
        </div>

        <p><b>ID:</b> {offer.offer_id}</p>
        <p><b>Units:</b> {offer.units} kWh</p>
        <p><b>Rate:</b> {offer.token_per_unit} Tokens/unit</p>
        <p className="text-yellow-300 font-bold mt-2">
          Total: {offer.total_tokens}
        </p>

        {offer.status === "negotiation" && (
          <div className="mt-3 p-3 bg-yellow-700/40 rounded-lg">
            <p className="font-bold text-yellow-200">
              Negotiated Price: {offer.negotiated_tokens} Tokens
            </p>
            <p className="text-xs text-white/70">
              By: {offer.negotiated_by}
            </p>
          </div>
        )}

        <p className="text-xs text-white/70 mt-3 flex items-center gap-1">
          <UserIcon className="w-4 h-4" /> {offer.creator_id}
        </p>
        <p className="text-xs text-white/70 flex items-center gap-1">
          <ClockIcon className="w-4 h-4" /> {created}
        </p>

        {/* ACTION BUTTONS */}
        {isOwnOffer && (
          <div className="mt-3 space-y-2">
            {offer.status === "open" && (
              <button
                onClick={() => handleCancelOffer(offer.offer_id)}
                className="w-full bg-red-500 hover:bg-red-600 text-white font-bold py-2 rounded-lg"
              >
                Cancel Offer
              </button>
            )}

            {offer.status === "negotiation" && (
              <>
                <button
                  onClick={() => handleAcceptNegotiation(offer.offer_id)}
                  className="w-full bg-green-500 hover:bg-green-600 text-white font-bold py-2 rounded-lg"
                >
                  Accept Negotiation
                </button>

                <button
                  onClick={() => handleCancelNegotiation(offer.offer_id)}
                  className="w-full bg-red-500 hover:bg-red-600 text-white font-bold py-2 rounded-lg"
                >
                  Cancel Negotiation
                </button>
              </>
            )}
          </div>
        )}
      </div>
    );
  };

  // -----------------------------------
  // FILTERS
  // -----------------------------------
  const loggedId = localStorage.getItem("user_id");
  const loggedTransformer = localStorage.getItem("transformer_id");

  const myActiveOffers = useMemo(() => {
    return myOffers
      .filter(
        (o) =>
          o.creator_id === loggedId &&
          (o.status === "open" || o.status === "negotiation")
      )
      .sort((a, b) => new Date(b.created_at) - new Date(a.created_at));
  }, [myOffers, loggedId]);

  const filteredMarketOffers = useMemo(() => {
    return marketOffers.filter((o) => {
      if (o.creator_id === loggedId) return false;
      if (o.status !== "open") return false;
      if (o.transformer_id !== loggedTransformer) return false;
      return true;
    });
  }, [marketOffers, loggedTransformer, loggedId]);

  // -----------------------------------
  // RENDER
  // -----------------------------------
  return (
    <div className="min-h-screen bg-gradient-to-b from-green-600 to-green-800 text-white p-6 pb-24 font-inter">
      <h1 className="text-3xl font-bold text-center mb-6 flex items-center justify-center gap-2">
        <BoltIcon className="w-8 h-8 text-yellow-300" />
        Energy Trading
      </h1>

      {error && (
        <div className="p-3 bg-red-800/80 mb-4 rounded-lg font-semibold shadow-inner">
          ⚠ {error}
        </div>
      )}

      {/* CREATE OFFER */}
      <div className="bg-green-700/70 rounded-xl p-5 mb-8 shadow-2xl border-2 border-green-400/50">
        <h2 className="font-bold text-xl mb-3 border-b border-green-500 pb-2">
          Create New Offer
        </h2>

        <form
          className="grid grid-cols-2 md:grid-cols-5 gap-3 items-end"
          onSubmit={handleCreateOffer}
        >
          <div>
            <label className="block mb-1 text-sm font-medium">Type</label>
            <select
              value={newOffer.offer_type}
              onChange={(e) =>
                setNewOffer({ ...newOffer, offer_type: e.target.value })
              }
              className="text-gray-800 px-3 py-2 w-full rounded-lg bg-white"
            >
              <option value="sell">Sell</option>
              <option value="buy">Buy</option>
            </select>
          </div>

          <div>
            <label className="block mb-1 text-sm font-medium">Units (kWh)</label>
            <input
              type="number"
              value={newOffer.units}
              onChange={(e) =>
                setNewOffer({ ...newOffer, units: e.target.value })
              }
              className="text-gray-800 px-3 py-2 w-full rounded-lg bg-white"
            />
          </div>

          <div>
            <label className="block mb-1 text-sm font-medium">Token / Unit</label>
            <input
              type="number"
              value={newOffer.token_per_unit}
              onChange={(e) =>
                setNewOffer({ ...newOffer, token_per_unit: e.target.value })
              }
              className="text-gray-800 px-3 py-2 w-full rounded-lg bg-white"
            />
          </div>

          <div className="text-center p-2 bg-green-800/40 rounded-lg">
            <p className="text-xs text-green-200">Total</p>
            <p className="text-lg font-bold text-yellow-300">
              {totalTokens.toFixed(2)} Tokens
            </p>
          </div>

          <button
            type="submit"
            className="bg-yellow-400 hover:bg-yellow-500 text-gray-900 font-bold px-4 py-2 rounded-lg"
          >
            Create Offer
          </button>
        </form>
      </div>

      {/* MY OFFERS */}
      <h2 className="text-2xl font-bold text-yellow-300 text-center mb-3">
        Your Active Offers
      </h2>

      {loading ? (
        <p className="text-center text-xl p-6">
          <ClockIcon className="w-6 h-6 inline-block animate-spin mr-2" />
          Loading...
        </p>
      ) : myActiveOffers.length === 0 ? (
        <p className="text-center text-lg p-4 bg-green-700/40 rounded-lg">
          No active offers.
        </p>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-5 mb-10">
          {myActiveOffers.map((offer) => (
            <OfferCard
              key={offer.offer_id}
              offer={offer}
              isOwnOffer={true}
            />
          ))}
        </div>
      )}

      {/* MARKET OFFERS */}
      <h2 className="text-2xl font-bold text-white text-center mb-3">
        Market Offers Near You
      </h2>

      {filteredMarketOffers.length === 0 ? (
        <p className="text-center text-lg p-4 bg-green-700/40 rounded-lg">
          No available offers.
        </p>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-5">
          {filteredMarketOffers.map((offer) => (
            <OfferCard
              key={offer.offer_id}
              offer={offer}
              isOwnOffer={false}
            />
          ))}
        </div>
      )}

      <NavigationBar active="Trade" />
    </div>
  );
}
