import React, { useState } from "react";
import { loginUser } from "../api";
import { useNavigate } from "react-router-dom";
import { Eye, EyeOff } from "lucide-react";  // ðŸ‘ˆ Add this

export default function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false); // ðŸ‘ˆ Add this
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleLogin = async () => {
    setError("");

    if (!email) {
  setError("Please enter your email.");
  return;
}

if (!password) {
  setError("Please enter your password.");
  return;
}


    const data = await loginUser(email, password);

    if (data.token) {
      localStorage.setItem("token", data.token);
      navigate("/dashboard");
    } else {
      setError(data.message || "Login failed");
    }
  };

  return (
    <div className="p-10 flex flex-col items-center justify-center min-h-screen bg-gray-100">
      <div className="bg-white p-8 rounded-xl w-96 shadow">
        <h1 className="text-2xl font-bold mb-5 text-center">Login</h1>

        <input
          className="border p-2 w-full mb-3 rounded"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />

        {/* ðŸ‘‡ Password input with eye toggle */}
        <div className="relative">
          <input
            className="border p-2 w-full mb-3 rounded pr-10"
            placeholder="Password"
            type={showPassword ? "text" : "password"}   // ðŸ‘ˆ Toggle
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />

          {/* Eye icon */}
          <div
            className="absolute right-3 top-3 cursor-pointer text-gray-600"
            onClick={() => setShowPassword(!showPassword)}
          >
            {showPassword ? <EyeOff size={20} /> : <Eye size={20} />}
          </div>
        </div>

        {error && <p className="text-red-500 text-sm mb-3">{error}</p>}

        <button
          className="bg-blue-600 text-white p-2 rounded w-full hover:bg-blue-700"
          onClick={handleLogin}
        >
          Login
        </button>

        <p className="mt-3 text-center">
          New user?{" "}
          <a className="text-blue-600 hover:underline" href="/register">
            Register
          </a>
        </p>
      </div>
    </div>
  );
}
