import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

export default function Profile() {
  const navigate = useNavigate();
  const [profile, setProfile] = useState(null);
  const [balances, setBalances] = useState(null);
  const [technical, setTechnical] = useState(null);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(true);

  // Change password form
  const [currentPassword, setCurrentPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [passwordMsg, setPasswordMsg] = useState("");

  const token = localStorage.getItem("token");

  // Redirect if no token
  useEffect(() => {
    if (!token) {
      navigate("/login");
    }
  }, [token, navigate]);

  // Fetch profile, balances, technical info
  const fetchData = async () => {
    try {
      const headers = { Authorization: `Bearer ${token}` };

      const resProfile = await fetch("http://localhost:3000/api/profile", { headers });
      const dataProfile = await resProfile.json();
      if (dataProfile.success) setProfile(dataProfile.data);

      const resBalances = await fetch("http://localhost:3000/api/profile/balances", { headers });
      const dataBalances = await resBalances.json();
      if (dataBalances.success) setBalances(dataBalances.data);

      const resTech = await fetch("http://localhost:3000/api/profile/technical", { headers });
      const dataTech = await resTech.json();
      if (dataTech.success) setTechnical(dataTech.data);

    } catch (err) {
      console.error(err);
      setError("Failed to fetch profile data.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  // Handle change password
  const handleChangePassword = async () => {
    if (!currentPassword || !newPassword) {
      setPasswordMsg("Please fill both fields.");
      return;
    }
    try {
      const res = await fetch("http://localhost:4000/api/auth/change-password", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ currentPassword, newPassword }),
      });
      const data = await res.json();
      setPasswordMsg(data.message);
      setCurrentPassword("");
      setNewPassword("");
    } catch (err) {
      console.error(err);
      setPasswordMsg("Failed to change password.");
    }
  };

  if (loading) return <div className="p-6 text-center">Loading...</div>;
  if (error) return <div className="p-6 text-red-600">{error}</div>;

  return (
    <div className="p-6 max-w-3xl mx-auto space-y-6">
      <div className="bg-white p-6 rounded shadow">
        <h1 className="text-2xl font-bold mb-4">Profile</h1>
        <p><strong>Name:</strong> {profile?.name}</p>
        <p><strong>Email:</strong> {profile?.email}</p>
      </div>

      <div className="bg-white p-6 rounded shadow">
        <h2 className="text-xl font-semibold mb-4">Balances</h2>
        <p><strong>Wallet Balance:</strong> {balances?.wallet_balance}</p>
        <p><strong>Token Balance:</strong> {balances?.token_balance}</p>
        <p><strong>Energy Balance:</strong> {balances?.energy_balance}</p>
      </div>

      <div className="bg-white p-6 rounded shadow">
        <h2 className="text-xl font-semibold mb-4">Technical Info</h2>
        <p><strong>Meter ID:</strong> {technical?.meterId}</p>
        <p><strong>Transformer ID:</strong> {technical?.transformerId}</p>
      </div>

      <div className="bg-white p-6 rounded shadow">
        <h2 className="text-xl font-semibold mb-4">Change Password</h2>
        {passwordMsg && <p className="text-sm text-red-600 mb-2">{passwordMsg}</p>}
        <input
          type="password"
          placeholder="Current Password"
          className="border p-3 w-full rounded mb-2"
          value={currentPassword}
          onChange={(e) => setCurrentPassword(e.target.value)}
        />
        <input
          type="password"
          placeholder="New Password"
          className="border p-3 w-full rounded mb-2"
          value={newPassword}
          onChange={(e) => setNewPassword(e.target.value)}
        />
        <button
          className="bg-blue-600 text-white p-3 rounded w-full hover:bg-blue-700 transition"
          onClick={handleChangePassword}
        >
          Change Password
        </button>
      </div>
    </div>
  );
}
