import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { WalletIcon, CubeIcon, LightningBoltIcon, UserIcon, CogIcon, KeyIcon } from "@heroicons/react/24/outline";

export default function Profile() {
  const navigate = useNavigate();

  const [profile, setProfile] = useState(null);
  const [balances, setBalances] = useState(null);
  const [technical, setTechnical] = useState(null);
  const [errors, setErrors] = useState({ profile: "", balances: "", technical: "" });
  const [loading, setLoading] = useState(true);

  const [currentPassword, setCurrentPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [passwordMsg, setPasswordMsg] = useState("");

  const token = localStorage.getItem("token");

  useEffect(() => {
    if (!token) navigate("/login");
  }, [token, navigate]);

  const fetchData = async () => {
    if (!token) return;
    setLoading(true);

    // Profile
    try {
      const res = await fetch("http://localhost:5001/api/auth/profile", {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      if (data.success) setProfile(data.data);
      else setErrors((prev) => ({ ...prev, profile: data.message || "Failed to fetch profile" }));
    } catch {
      setErrors((prev) => ({ ...prev, profile: "Failed to fetch profile" }));
    }

    // Balances
    try {
      const res = await fetch("http://localhost:5001/api/auth/profile/balances", {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      if (data.success) setBalances(data.data);
      else setErrors((prev) => ({ ...prev, balances: data.message || "Failed to fetch balances" }));
    } catch {
      setErrors((prev) => ({ ...prev, balances: "Failed to fetch balances" }));
    }

    // Technical
    try {
      const res = await fetch("http://localhost:5001/api/auth/profile/technical", {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      if (data.success) setTechnical(data.data);
      else setErrors((prev) => ({ ...prev, technical: data.message || "Failed to fetch technical info" }));
    } catch {
      setErrors((prev) => ({ ...prev, technical: "Failed to fetch technical info" }));
    }

    setLoading(false);
  };

  useEffect(() => {
    fetchData();
  }, []);

  const handleChangePassword = async () => {
    if (!currentPassword || !newPassword) {
      setPasswordMsg("Please fill both fields.");
      return;
    }
    try {
      const res = await fetch("http://localhost:5001/api/auth/change-password", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ currentPassword, newPassword }),
      });
      const data = await res.json();
      setPasswordMsg(data.message || "Password change failed");
      setCurrentPassword("");
      setNewPassword("");
    } catch {
      setPasswordMsg("Failed to change password.");
    }
  };

  if (loading) return <div className="p-6 text-center text-gray-500">Loading...</div>;

  return (
    <div className="p-6 max-w-4xl mx-auto space-y-6">

      {/* Profile Card */}
      <div className="bg-gradient-to-r from-indigo-500 to-purple-500 text-white p-6 rounded-xl shadow-lg flex items-center space-x-4">
        <UserIcon className="w-12 h-12" />
        <div>
          <h1 className="text-2xl font-bold">{profile?.name || "-"}</h1>
          <p className="text-sm">{profile?.email || "-"}</p>
          {errors.profile && <p className="text-red-200 mt-1">{errors.profile}</p>}
        </div>
      </div>

      {/* Balances Card */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        <div className="bg-white p-6 rounded-xl shadow hover:shadow-lg transition">
          <div className="flex items-center space-x-2 mb-2">
            <WalletIcon className="w-6 h-6 text-indigo-500" />
            <h2 className="font-semibold text-lg">Wallet Balance</h2>
          </div>
          {errors.balances && <p className="text-red-600 text-sm">{errors.balances}</p>}
          <p className="text-xl font-bold">{balances?.wallet_balance ?? "-"}</p>
        </div>

        <div className="bg-white p-6 rounded-xl shadow hover:shadow-lg transition">
          <div className="flex items-center space-x-2 mb-2">
            <CubeIcon className="w-6 h-6 text-green-500" />
            <h2 className="font-semibold text-lg">Token Balance</h2>
          </div>
          <p className="text-xl font-bold">{balances?.token_balance ?? "-"}</p>
        </div>

        <div className="bg-white p-6 rounded-xl shadow hover:shadow-lg transition">
          <div className="flex items-center space-x-2 mb-2">
            <LightningBoltIcon className="w-6 h-6 text-yellow-500" />
            <h2 className="font-semibold text-lg">Energy Balance</h2>
          </div>
          <p className="text-xl font-bold">{balances?.energy_balance ?? "-"}</p>
        </div>
      </div>

      {/* Technical Info */}
      <div className="bg-white p-6 rounded-xl shadow">
        <div className="flex items-center space-x-2 mb-4">
          <CogIcon className="w-6 h-6 text-gray-700" />
          <h2 className="font-semibold text-lg">Technical Info</h2>
        </div>
        {errors.technical && <p className="text-red-600 mb-2">{errors.technical}</p>}
        <p><strong>Meter ID:</strong> {technical?.meterId || "-"}</p>
        <p><strong>Transformer ID:</strong> {technical?.transformerId || "-"}</p>
      </div>

      {/* Change Password */}
      <div className="bg-white p-6 rounded-xl shadow">
        <div className="flex items-center space-x-2 mb-4">
          <KeyIcon className="w-6 h-6 text-red-500" />
          <h2 className="font-semibold text-lg">Change Password</h2>
        </div>
        {passwordMsg && <p className="text-sm text-red-600 mb-2">{passwordMsg}</p>}
        <input
          type="password"
          placeholder="Current Password"
          className="border p-3 w-full rounded mb-2 focus:outline-none focus:ring-2 focus:ring-indigo-400"
          value={currentPassword}
          onChange={(e) => setCurrentPassword(e.target.value)}
        />
        <input
          type="password"
          placeholder="New Password"
          className="border p-3 w-full rounded mb-2 focus:outline-none focus:ring-2 focus:ring-indigo-400"
          value={newPassword}
          onChange={(e) => setNewPassword(e.target.value)}
        />
        <button
          className="bg-indigo-500 text-white p-3 rounded w-full hover:bg-indigo-600 transition font-semibold"
          onClick={handleChangePassword}
        >
          Change Password
        </button>
      </div>
    </div>
  );
}
