import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

// Fetch user profile from backend
const fetchUserProfile = async (token) => {
  try {
    const response = await fetch("http://localhost:5001/api/profile", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`, // send token
      },
    });

    if (!response.ok) {
      const errorData = await response.json();
      return { error: errorData.message || "Failed to fetch profile" };
    }

    return await response.json();
  } catch (err) {
    console.error("Profile fetch error:", err);
    return { error: "Cannot reach server. Please try again later." };
  }
};

export default function Profile() {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [error, setError] = useState("");

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/login");
      return;
    }

    const getUserProfile = async () => {
      const data = await fetchUserProfile(token);
      if (data.error) {
        setError(data.error);
      } else {
        setUser(data);
      }
    };

    getUserProfile();
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  if (error) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-gray-100 p-4">
        <div className="bg-white p-6 rounded-xl shadow-md w-full max-w-md text-center">
          <p className="text-red-500 mb-4">{error}</p>
          <button
            onClick={() => navigate("/login")}
            className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
          >
            Go to Login
          </button>
        </div>
      </div>
    );
  }

  if (!user) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-gray-100">
        <p className="text-gray-600 text-lg">Loading profile...</p>
      </div>
    );
  }

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100 p-4">
      <div className="bg-white p-6 sm:p-8 rounded-xl shadow-md w-full max-w-md">
        <h1 className="text-2xl font-bold mb-6 text-center">Profile</h1>

        <div className="mb-4">
          <p className="font-semibold">Name:</p>
          <p className="text-gray-700">{user.name}</p>
        </div>

        <div className="mb-4">
          <p className="font-semibold">Email:</p>
          <p className="text-gray-700">{user.email}</p>
        </div>

        <div className="mb-4">
          <p className="font-semibold">Joined On:</p>
          <p className="text-gray-700">{new Date(user.createdAt).toLocaleDateString()}</p>
        </div>

        <button
          onClick={handleLogout}
          className="bg-red-500 text-white w-full p-3 rounded hover:bg-red-600 transition-colors"
        >
          Logout
        </button>
      </div>
    </div>
  );
}
