import React, { useEffect, useState } from "react";
import { WalletIcon, CubeIcon, BoltIcon, UserIcon, CogIcon, KeyIcon, LogoutIcon } from "@heroicons/react/24/outline";
import { useNavigate } from "react-router-dom";

export default function Profile() {
  const navigate = useNavigate();
  const token = localStorage.getItem("token");

  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  const [showChangePassword, setShowChangePassword] = useState(false);
  const [currentPassword, setCurrentPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [passwordMsg, setPasswordMsg] = useState("");

  // Redirect to login if no token
  useEffect(() => {
    if (!token) navigate("/login");
  }, [token, navigate]);

  // Fetch user profile data
  const fetchUserProfile = async () => {
    setLoading(true);
    setError("");
    try {
      const res = await fetch("http://localhost:5001/api/auth/profile", {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      if (data.success) {
        setUserData(data.data);
      } else {
        setError(data.message || "Failed to fetch profile");
      }
    } catch (err) {
      setError("Failed to fetch profile");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchUserProfile();
  }, []);

  const handleChangePassword = async () => {
    if (!currentPassword || !newPassword) {
      setPasswordMsg("Please fill both fields.");
      return;
    }
    setPasswordMsg("Processing...");
    try {
      const res = await fetch("http://localhost:5001/api/auth/change-password", {
        method: "POST",
        headers: { "Content-Type": "application/json", Authorization: `Bearer ${token}` },
        body: JSON.stringify({ currentPassword, newPassword }),
      });
      const data = await res.json();
      setPasswordMsg(data.message || "Failed to update password");
      setCurrentPassword("");
      setNewPassword("");
    } catch {
      setPasswordMsg("Failed to update password");
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50 text-gray-700 font-semibold">
        Loading profile...
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 p-6 pb-20 max-w-4xl mx-auto space-y-6">
      <div className="flex justify-between items-center">
        <h1 className="text-3xl font-bold text-gray-800 border-b pb-2 mb-4">Profile</h1>
        <button
          className="flex items-center space-x-1 bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded shadow"
          onClick={handleLogout}
        >
          <LogoutIcon className="w-5 h-5" />
          <span>Logout</span>
        </button>
      </div>

      {/* Personal Info */}
      <div className="bg-gradient-to-r from-emerald-600 to-green-700 text-white p-6 rounded-xl shadow-lg space-y-2">
        <h2 className="text-xl font-bold flex items-center space-x-2">
          <UserIcon className="w-6 h-6" />
          <span>Personal & Identity</span>
        </h2>
        {error && <p className="text-red-200 mt-1">{error}</p>}
        <p className="text-sm"><strong>Name:</strong> {userData?.name || "-"}</p>
        <p className="text-sm"><strong>Email:</strong> {userData?.email || "-"}</p>
        <p className="text-sm">
          <strong>User ID:</strong> 
          <span className="font-mono bg-white bg-opacity-20 p-1 rounded ml-1 text-xs">{userData?.userId || "-"}</span>
        </p>
      </div>

      {/* Main Balances */}
      <h2 className="text-xl font-bold text-gray-800 pt-2">Current Balances</h2>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        <div className="bg-white p-6 rounded-xl shadow border-l-4 border-indigo-500">
          <div className="flex items-center space-x-2 mb-2">
            <WalletIcon className="w-6 h-6 text-indigo-500" />
            <h3 className="font-semibold text-lg text-gray-700">Wallet Balance</h3>
          </div>
          <p className="text-2xl font-extrabold text-indigo-700">{userData?.wallet_balance ?? "-"}</p>
        </div>
        <div className="bg-white p-6 rounded-xl shadow border-l-4 border-teal-500">
          <div className="flex items-center space-x-2 mb-2">
            <CubeIcon className="w-6 h-6 text-teal-500" />
            <h3 className="font-semibold text-lg text-gray-700">Token Balance</h3>
          </div>
          <p className="text-2xl font-extrabold text-teal-700">{userData?.token_balance ?? "-"}</p>
        </div>
        <div className="bg-white p-6 rounded-xl shadow border-l-4 border-amber-500">
          <div className="flex items-center space-x-2 mb-2">
            <BoltIcon className="w-6 h-6 text-amber-500" />
            <h3 className="font-semibold text-lg text-gray-700">Energy Balance</h3>
          </div>
          <p className="text-2xl font-extrabold text-amber-700">{userData?.energy_balance ?? "-"}</p>
        </div>
      </div>

      {/* Reserved Balances */}
      <h2 className="text-xl font-bold text-gray-800 pt-4">Reserved Balances (In Trade)</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        <div className="bg-white p-6 rounded-xl shadow border-l-4 border-teal-400">
          <div className="flex items-center space-x-2 mb-2">
            <CubeIcon className="w-6 h-6 text-teal-500" />
            <h3 className="font-semibold text-lg text-gray-700">Reserved Token Balance</h3>
          </div>
          <p className="text-2xl font-extrabold text-teal-600">{userData?.reserved_token_balance ?? "-"}</p>
          <p className="text-xs text-gray-500">Tokens locked for active sell orders</p>
        </div>
        <div className="bg-white p-6 rounded-xl shadow border-l-4 border-amber-400">
          <div className="flex items-center space-x-2 mb-2">
            <BoltIcon className="w-6 h-6 text-amber-500" />
            <h3 className="font-semibold text-lg text-gray-700">Reserved Energy Balance</h3>
          </div>
          <p className="text-2xl font-extrabold text-amber-600">{userData?.reserved_energy_balance ?? "-"}</p>
          <p className="text-xs text-gray-500">Energy allocated for delivery contracts</p>
        </div>
      </div>

      {/* Technical Info */}
      <div className="bg-white p-6 rounded-xl shadow">
        <div className="flex items-center space-x-2 mb-4 border-b pb-2">
          <CogIcon className="w-6 h-6 text-gray-700" />
          <h3 className="font-semibold text-xl text-gray-800">Technical Information</h3>
        </div>
        <div className="grid grid-cols-2 gap-4 text-gray-700">
          <div>
            <p className="mb-2"><strong>Meter ID:</strong> <span className="font-mono bg-gray-100 p-1 rounded text-sm">{userData?.meterId ?? "-"}</span></p>
            <p><strong>Transformer ID:</strong> <span className="font-mono bg-gray-100 p-1 rounded text-sm">{userData?.transformerId ?? "-"}</span></p>
          </div>
          <div>
            <p className="mb-2"><strong>Total Energy Sold:</strong> <span className="font-bold text-emerald-600">{userData?.total_energy_sold ?? "-"}</span></p>
            <p><strong>Total Energy Bought:</strong> <span className="font-bold text-indigo-600">{userData?.total_energy_bought ?? "-"}</span></p>
          </div>
        </div>
      </div>

      {/* Change Password Section (Toggleable) */}
      <div className="bg-white p-6 rounded-xl shadow">
        <div className="flex justify-between items-center mb-4 border-b pb-2">
          <h3 className="font-semibold text-xl text-gray-800 flex items-center space-x-2">
            <KeyIcon className="w-6 h-6 text-red-500" />
            <span>Update Secure Key</span>
          </h3>
          <button
            className="text-sm text-emerald-600 hover:underline"
            onClick={() => setShowChangePassword(!showChangePassword)}
          >
            {showChangePassword ? "Cancel" : "Change Password"}
          </button>
        </div>
        {showChangePassword && (
          <>
            {passwordMsg && (
              <p className={`text-sm mb-2 ${passwordMsg.toLowerCase().includes("success") ? "text-green-600" : "text-red-600"}`}>
                {passwordMsg}
              </p>
            )}
            <input
              type="password"
              placeholder="Current Password"
              className="border p-3 w-full rounded-lg mb-3 focus:outline-none focus:ring-2 focus:ring-emerald-400 transition"
              value={currentPassword}
              onChange={(e) => setCurrentPassword(e.target.value)}
            />
            <input
              type="password"
              placeholder="New Password"
              className="border p-3 w-full rounded-lg mb-4 focus:outline-none focus:ring-2 focus:ring-emerald-400 transition"
              value={newPassword}
              onChange={(e) => setNewPassword(e.target.value)}
            />
            <button
              className="bg-emerald-600 text-white p-3 rounded-lg w-full hover:bg-emerald-700 transition font-semibold shadow-md"
              onClick={handleChangePassword}
            >
              Change Secure Key
            </button>
          </>
        )}
      </div>
    </div>
  );
}
