import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { WalletIcon, CubeIcon, BoltIcon, UserIcon, CogIcon, KeyIcon } from "@heroicons/react/24/outline";

const NavigationBar = ({ active }) => {
  const navigate = useNavigate();
  const NavItem = ({ name, icon, route }) => (
    <button
      aria-label={name}
      className={`flex flex-col items-center justify-center p-2 text-xs font-medium transition-colors ${
        active === name ? "text-emerald-600 border-t-2 border-emerald-600" : "text-gray-500 hover:text-emerald-500"
      }`}
      onClick={() => navigate(route)}
    >
      {icon}
      {name}
    </button>
  );

  return (
    <div className="fixed inset-x-0 bottom-0 bg-white border-t border-gray-200 shadow-2xl z-50">
      <div className="max-w-4xl mx-auto flex justify-around">
        <NavItem name="Dashboard" icon={<BoltIcon className="w-5 h-5" />} route="/dashboard" />
        <NavItem name="Trade" icon={<CubeIcon className="w-5 h-5" />} route="/trade" />
        <NavItem name="History" icon={<WalletIcon className="w-5 h-5" />} route="/history" />
        <NavItem name="Profile" icon={<UserIcon className="w-5 h-5" />} route="/profile" />
      </div>
    </div>
  );
};

// Skeleton Loader
const Skeleton = ({ className }) => <div className={`bg-gray-200 animate-pulse rounded-xl ${className}`} />;

export default function Profile() {
  const navigate = useNavigate();
  const [profile, setProfile] = useState(null);
  const [balances, setBalances] = useState(null);
  const [technical, setTechnical] = useState(null);
  const [errors, setErrors] = useState({ profile: "", balances: "", technical: "" });
  const [loading, setLoading] = useState(true);
  const [passwordMsg, setPasswordMsg] = useState("");
  const [currentPassword, setCurrentPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");

  const token = localStorage.getItem("token");

  useEffect(() => {
    if (!token) {
      navigate("/login");
    }
  }, [token, navigate]);

  const fetchData = async () => {
    setLoading(true);
    try {
      // Profile
      const resProfile = await fetch("http://localhost:5001/api/auth/profile", {
        headers: { Authorization: `Bearer ${token}` },
      });
      const dataProfile = await resProfile.json();
      if (resProfile.ok && dataProfile.success) setProfile(dataProfile.data);
      else setErrors((prev) => ({ ...prev, profile: dataProfile.message || "Failed to fetch profile" }));

      // Balances
      const resBalances = await fetch("http://localhost:5001/api/auth/profile/balances", {
        headers: { Authorization: `Bearer ${token}` },
      });
      const dataBalances = await resBalances.json();
      if (resBalances.ok && dataBalances.success) setBalances(dataBalances.data);
      else setErrors((prev) => ({ ...prev, balances: dataBalances.message || "Failed to fetch balances" }));

      // Technical
      const resTechnical = await fetch("http://localhost:5001/api/auth/profile/technical", {
        headers: { Authorization: `Bearer ${token}` },
      });
      const dataTechnical = await resTechnical.json();
      if (resTechnical.ok && dataTechnical.success) setTechnical(dataTechnical.data);
      else setErrors((prev) => ({ ...prev, technical: dataTechnical.message || "Failed to fetch technical info" }));
    } catch (err) {
      console.error(err);
      setErrors({
        profile: "Failed to fetch profile",
        balances: "Failed to fetch balances",
        technical: "Failed to fetch technical info",
      });
    }
    setLoading(false);
  };

  useEffect(() => {
    fetchData();
  }, []);

  const handleChangePassword = async () => {
    if (!currentPassword || !newPassword) {
      setPasswordMsg("Please fill both fields.");
      return;
    }
    setPasswordMsg("Processing...");
    try {
      const res = await fetch("http://localhost:5001/api/auth/change-password", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ currentPassword, newPassword }),
      });
      const data = await res.json();
      setPasswordMsg(data.message || "Failed to update password");
      setCurrentPassword("");
      setNewPassword("");
    } catch {
      setPasswordMsg("Network error. Try again.");
    }
  };

  return (
    <div className="p-6 pb-20 max-w-4xl mx-auto space-y-6 bg-gray-50 min-h-screen">
      <h1 className="text-3xl font-bold text-gray-800 border-b pb-2 mb-4">Profile</h1>

      {/* Personal Info */}
      <div className="bg-gradient-to-r from-emerald-600 to-green-700 text-white p-6 rounded-xl shadow-lg space-y-2">
        <h2 className="text-xl font-bold flex items-center space-x-2">
          <UserIcon className="w-6 h-6" />
          <span>Personal & Identity</span>
        </h2>
        {errors.profile && <p className="text-red-200 mt-1">{errors.profile}</p>}
        {loading ? (
          <Skeleton className="h-6 w-1/3" />
        ) : (
          <>
            <p><strong>Name:</strong> {profile?.name}</p>
            <p><strong>Email:</strong> {profile?.email}</p>
            <p><strong>User ID:</strong> {profile?.userId}</p>
          </>
        )}
      </div>

      {/* Balances */}
      <h2 className="text-xl font-bold text-gray-800 pt-2">Current Balances</h2>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        {loading ? (
          <>
            <Skeleton className="h-24" />
            <Skeleton className="h-24" />
            <Skeleton className="h-24" />
          </>
        ) : (
          <>
            <div className="bg-white p-6 rounded-xl shadow border-l-4 border-indigo-500">
              <div className="flex items-center space-x-2 mb-2">
                <WalletIcon className="w-6 h-6 text-indigo-500" />
                <h3 className="font-semibold text-lg text-gray-700">Wallet Balance</h3>
              </div>
              {errors.balances && <p className="text-red-600 text-sm">{errors.balances}</p>}
              <p className="text-2xl font-extrabold text-indigo-700">{balances?.wallet_balance}</p>
            </div>
            <div className="bg-white p-6 rounded-xl shadow border-l-4 border-teal-500">
              <div className="flex items-center space-x-2 mb-2">
                <CubeIcon className="w-6 h-6 text-teal-500" />
                <h3 className="font-semibold text-lg text-gray-700">Token Balance</h3>
              </div>
              <p className="text-2xl font-extrabold text-teal-700">{balances?.token_balance}</p>
            </div>
            <div className="bg-white p-6 rounded-xl shadow border-l-4 border-amber-500">
              <div className="flex items-center space-x-2 mb-2">
                <BoltIcon className="w-6 h-6 text-amber-500" />
                <h3 className="font-semibold text-lg text-gray-700">Energy Balance</h3>
              </div>
              <p className="text-2xl font-extrabold text-amber-700">{balances?.energy_balance}</p>
            </div>
          </>
        )}
      </div>

      {/* Technical Info */}
      <div className="bg-white p-6 rounded-xl shadow">
        <div className="flex items-center space-x-2 mb-4 border-b pb-2">
          <CogIcon className="w-6 h-6 text-gray-700" />
          <h3 className="font-semibold text-xl text-gray-800">Technical Info</h3>
        </div>
        {loading ? (
          <Skeleton className="h-24" />
        ) : (
          <>
            {errors.technical && <p className="text-red-600 mb-2">{errors.technical}</p>}
            <p><strong>Meter ID:</strong> {technical?.meterId}</p>
            <p><strong>Transformer ID:</strong> {technical?.transformerId}</p>
            <p><strong>Total Energy Sold:</strong> {technical?.totalEnergySold}</p>
            <p><strong>Total Energy Bought:</strong> {technical?.totalEnergyBought}</p>
          </>
        )}
      </div>

      {/* Change Password */}
      <div className="bg-white p-6 rounded-xl shadow">
        <div className="flex items-center space-x-2 mb-4 border-b pb-2">
          <KeyIcon className="w-6 h-6 text-red-500" />
          <h3 className="font-semibold text-xl text-gray-800">Change Password</h3>
        </div>
        {passwordMsg && <p className={`text-sm mb-2 ${passwordMsg.toLowerCase().includes("success") ? "text-green-600" : "text-red-600"}`}>{passwordMsg}</p>}
        <input type="password" placeholder="Current Password" className="border p-3 w-full rounded mb-3" value={currentPassword} onChange={(e) => setCurrentPassword(e.target.value)} />
        <input type="password" placeholder="New Password" className="border p-3 w-full rounded mb-4" value={newPassword} onChange={(e) => setNewPassword(e.target.value)} />
        <button className="bg-emerald-600 text-white p-3 rounded w-full hover:bg-emerald-700 transition" onClick={handleChangePassword}>{passwordMsg === "Processing..." ? "Updating..." : "Change Password"}</button>
      </div>

      <NavigationBar active="Profile" />
    </div>
  );
}
