import React, { useEffect, useState } from "react";
import { WalletIcon, CubeIcon, BoltIcon, UserIcon, CogIcon, KeyIcon, ArrowRightOnRectangleIcon } from "@heroicons/react/24/outline";
import { useNavigate } from "react-router-dom";

export default function Profile() {
  const navigate = useNavigate();
  const [profile, setProfile] = useState(null);
  const [balances, setBalances] = useState(null);
  const [technical, setTechnical] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  const [showPasswordForm, setShowPasswordForm] = useState(false);
  const [currentPassword, setCurrentPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [passwordMsg, setPasswordMsg] = useState("");

  const token = localStorage.getItem("token");

  // Redirect to login if no token
  useEffect(() => {
    if (!token) navigate("/login");
  }, [token, navigate]);

  // Fetch profile data
  const fetchData = async () => {
    setLoading(true);
    setError("");

    try {
      const res = await fetch("http://localhost:5001/api/auth/profile", {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      if (data.success) {
        setProfile(data.data);
        setBalances({
          wallet_balance: data.data.wallet_balance,
          token_balance: data.data.token_balance,
          energy_balance: data.data.energy_balance,
          reserved_token_balance: data.data.reserved_token_balance,
          reserved_energy_balance: data.data.reserved_energy_balance,
        });
        setTechnical({
          meterId: data.data.meterId,
          transformerId: data.data.transformerId,
        });
      } else {
        setError(data.message || "Failed to fetch data.");
      }
    } catch (err) {
      console.error(err);
      setError("Failed to fetch profile data.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (token) fetchData();
  }, [token]);

  // Change password
  const handleChangePassword = async () => {
    if (!currentPassword || !newPassword) {
      setPasswordMsg("Please fill both fields.");
      return;
    }
    setPasswordMsg("Processing...");
    try {
      const res = await fetch("http://localhost:5001/api/auth/change-password", {
        method: "POST",
        headers: { "Content-Type": "application/json", Authorization: `Bearer ${token}` },
        body: JSON.stringify({ currentPassword, newPassword }),
      });
      const data = await res.json();
      setPasswordMsg(data.message || "Failed to change password.");
      setCurrentPassword("");
      setNewPassword("");
    } catch (err) {
      console.error(err);
      setPasswordMsg("Failed to change password.");
    }
  };

  // Logout
  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  if (loading) return <div className="min-h-screen flex items-center justify-center text-xl font-semibold">Loading...</div>;

  return (
    <div className="p-6 max-w-4xl mx-auto space-y-6 bg-gray-50 min-h-screen">
      {/* Header */}
      <div className="flex justify-between items-center mb-4">
        <h1 className="text-3xl font-bold text-gray-800">Profile</h1>
        <button
          className="flex items-center space-x-1 bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded shadow"
          onClick={handleLogout}
        >
          <ArrowRightOnRectangleIcon className="w-5 h-5" />
          <span>Logout</span>
        </button>
      </div>

      {/* Profile Card */}
      <div className="bg-white p-6 rounded-xl shadow space-y-2">
        <h2 className="text-xl font-bold flex items-center space-x-2">
          <UserIcon className="w-6 h-6" />
          <span>Personal Info</span>
        </h2>
        {error && <p className="text-red-600">{error}</p>}
        <p><strong>Name:</strong> {profile?.name || "-"}</p>
        <p><strong>Email:</strong> {profile?.email || "-"}</p>
        <p><strong>User ID:</strong> {profile?.userId || "-"}</p>
      </div>

      {/* Balances */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        <div className="bg-white p-6 rounded-xl shadow border-l-4 border-indigo-500">
          <div className="flex items-center space-x-2 mb-2"><WalletIcon className="w-6 h-6 text-indigo-500" /><h3>Wallet</h3></div>
          <p className="text-2xl font-bold">{balances?.wallet_balance ?? "-"}</p>
        </div>
        <div className="bg-white p-6 rounded-xl shadow border-l-4 border-teal-500">
          <div className="flex items-center space-x-2 mb-2"><CubeIcon className="w-6 h-6 text-teal-500" /><h3>Token</h3></div>
          <p className="text-2xl font-bold">{balances?.token_balance ?? "-"}</p>
        </div>
        <div className="bg-white p-6 rounded-xl shadow border-l-4 border-amber-500">
          <div className="flex items-center space-x-2 mb-2"><BoltIcon className="w-6 h-6 text-amber-500" /><h3>Energy</h3></div>
          <p className="text-2xl font-bold">{balances?.energy_balance ?? "-"}</p>
        </div>
      </div>

      {/* Reserved Balances */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mt-4">
        <div className="bg-white p-6 rounded-xl shadow border-l-4 border-teal-400">
          <h3>Reserved Tokens</h3>
          <p className="text-xl font-bold">{balances?.reserved_token_balance ?? "-"}</p>
        </div>
        <div className="bg-white p-6 rounded-xl shadow border-l-4 border-amber-400">
          <h3>Reserved Energy</h3>
          <p className="text-xl font-bold">{balances?.reserved_energy_balance ?? "-"}</p>
        </div>
      </div>

      {/* Technical Info */}
      <div className="bg-white p-6 rounded-xl shadow mt-4">
        <h2 className="text-xl font-bold flex items-center space-x-2">
          <CogIcon className="w-6 h-6" />
          <span>Technical Info</span>
        </h2>
        <p><strong>Meter ID:</strong> {technical?.meterId ?? "-"}</p>
        <p><strong>Transformer ID:</strong> {technical?.transformerId ?? "-"}</p>
      </div>

      {/* Change Password */}
      <div className="bg-white p-6 rounded-xl shadow mt-4">
        <button
          className="bg-blue-600 text-white px-4 py-2 rounded shadow hover:bg-blue-700"
          onClick={() => setShowPasswordForm(!showPasswordForm)}
        >
          {showPasswordForm ? "Cancel Change Password" : "Change Password"}
        </button>

        {showPasswordForm && (
          <div className="mt-4 space-y-2">
            {passwordMsg && <p className={`text-sm ${passwordMsg.includes("success") ? "text-green-600" : "text-red-600"}`}>{passwordMsg}</p>}
            <input
              type="password"
              placeholder="Current Password"
              className="border p-3 w-full rounded"
              value={currentPassword}
              onChange={(e) => setCurrentPassword(e.target.value)}
            />
            <input
              type="password"
              placeholder="New Password"
              className="border p-3 w-full rounded"
              value={newPassword}
              onChange={(e) => setNewPassword(e.target.value)}
            />
            <button
              className="bg-emerald-600 text-white p-3 rounded w-full hover:bg-emerald-700 transition font-semibold"
              onClick={handleChangePassword}
            >
              Update Password
            </button>
          </div>
        )}
      </div>
    </div>
  );
}
