import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";
// Ensure you have configured src/socket.js with autoConnect: false
import socket from "../socket"; 
import {
  BoltIcon,
  ClockIcon,
  UserIcon,
  ArrowRightIcon,
} from "@heroicons/react/24/outline";

import NavigationBar from "../components/navbar.jsx";

export default function TradePage() {
  const [offers, setOffers] = useState([]);
  const [newOffer, setNewOffer] = useState({
    offer_type: "sell",
    units: "",
    token_per_unit: "",
  });

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  // Use the local backend URL
  const API = "http://localhost:5001";

  // Helper function to get authorization headers
  const authHeader = () => {
    const token = localStorage.getItem("token");
    return {
      "Content-Type": "application/json",
      Authorization: `Bearer ${token}`,
    };
  };

  // -----------------------------
  // FETCH OFFERS 
  // NOTE: Requires a GET /api/offers/current endpoint on the backend
  // -----------------------------
  const fetchOffers = useCallback(async () => {
    try {
      setLoading(true);
      const res = await axios.get(`${API}/api/offers/current`, {
        headers: authHeader(),
      });

      // Assuming the backend returns the offer list in res.data.data or res.data
      setOffers(res.data.data || res.data || []); 
      setError("");
    } catch (err) {
      console.error("Fetch Offers Error:", err);
      const backendMessage = err.response?.data?.msg || "Server connection issue.";
      setError(`Failed to load offers: ${backendMessage}`);
    } finally {
      setLoading(false);
    }
  }, []);

  // -----------------------------
  // LIFECYCLE & SOCKET MANAGEMENT (Authentication Fix)
  // -----------------------------
  useEffect(() => {
    const token = localStorage.getItem("token");
    
    if (token) {
        // Authenticate and connect the socket using the token
        // This is CRUCIAL for real-time updates
        socket.auth = { token: token };
        socket.connect();
    } else {
        setError("Please log in to view and create offers.");
        setLoading(false);
    }
    
    // Initial fetch
    fetchOffers();

    // Listen for real-time update signals from the server
    socket.on("offersUpdated", fetchOffers);

    return () => {
      // Cleanup: Remove listener and disconnect socket on component unmount
      socket.off("offersUpdated", fetchOffers);
      if (socket.connected) {
          socket.disconnect();
      }
    };
  }, [fetchOffers]);

  // -----------------------------
  // CREATE OFFER
  // -----------------------------
  const handleCreateOffer = async (e) => {
    e.preventDefault();

    const creator_id = localStorage.getItem("user_id");
    
    // Check for "Creator not found" on the frontend
    if (!creator_id) {
      setError("User ID missing. Please log in again.");
      return;
    }

    if (!newOffer.units || !newOffer.token_per_unit) {
        setError("Please enter valid units and token rate.");
        return;
    }
    
    // Basic number validation
    const unitsNum = Number(newOffer.units);
    const tokenRateNum = Number(newOffer.token_per_unit);
    
    if (isNaN(unitsNum) || unitsNum <= 0 || isNaN(tokenRateNum) || tokenRateNum <= 0) {
        setError("Units and Token / Unit must be positive numbers.");
        return;
    }

    const offerBody = {
      creator_id,
      offer_type: newOffer.offer_type,
      units: unitsNum,
      token_per_unit: tokenRateNum,
    };

    try {
      // API call to POST /api/offers/create
      const res = await axios.post(`${API}/api/offers/create`, offerBody, {
        headers: authHeader(),
      });

      if (res.data.offer) { // Check for the created offer object
        // Success: Clear form and fetch updated offers
        setNewOffer({ offer_type: "sell", units: "", token_per_unit: "" });
        setError("");

        // Signal to the server to broadcast the update to everyone
        socket.emit("offerCreated"); 
        
        // Immediately update local state for fast UI feedback
        fetchOffers(); 
      } else {
        // Fallback for non-standard success response
        setError(res.data.message || "Offer created, but update failed.");
      }
    } catch (err) {
      console.error("Create Offer Error:", err);
      
      // CRUCIAL: Retrieve the specific error message from the backend response
      const backendMessage = err.response?.data?.msg;
      if (backendMessage) {
          setError(`Failed to create offer: ${backendMessage}`);
      } else {
          setError("Failed to create offer. Check network or server status.");
      }
    }
  };

  // -----------------------------
  // RENDER HELPERS
  // -----------------------------
  const totalTokens = (Number(newOffer.units) || 0) * (Number(newOffer.token_per_unit) || 0);
  const isBuy = newOffer.offer_type === 'buy';

  return (
    <div className="min-h-screen bg-gradient-to-b from-green-600 to-green-800 text-white p-6 pb-24 font-inter">
      <h1 className="text-3xl font-bold text-center mb-6 flex items-center justify-center gap-2">
        <BoltIcon className="w-8 h-8 text-yellow-300"/>
        Energy Trading
      </h1>

      {/* ERROR MESSAGE */}
      {error && (
        <div className="mb-4 p-3 bg-red-800/80 rounded-lg text-red-100 font-semibold shadow-inner transition-opacity duration-300">
          âš  {error}
        </div>
      )}

      {/* CREATE OFFER FORM */}
      <div className="bg-green-700/70 backdrop-blur-sm rounded-xl p-5 mb-8 shadow-2xl border-2 border-green-400/50">
        <h2 className="font-bold text-xl mb-3 border-b border-green-500 pb-2">Create New Offer</h2>
        <form className="grid grid-cols-2 md:grid-cols-5 gap-3 items-end" onSubmit={handleCreateOffer}>
          
          <div className="col-span-2 md:col-span-1">
            <label className="block mb-1 text-sm font-medium">Type</label>
            <select
              value={newOffer.offer_type}
              onChange={(e) => setNewOffer({ ...newOffer, offer_type: e.target.value })}
              className="text-gray-800 px-3 py-2 w-full rounded-lg bg-white/90 border border-gray-300 focus:ring-2 focus:ring-yellow-400"
            >
              <option value="sell">Sell (Energy)</option>
              <option value="buy">Buy (Energy)</option>
            </select>
          </div>

          <div className="col-span-1 md:col-span-1">
            <label className="block mb-1 text-sm font-medium">Units (kWh)</label>
            <input
              type="number"
              placeholder="Units"
              value={newOffer.units}
              onChange={(e) => setNewOffer({ ...newOffer, units: e.target.value.replace(/[^0-9.]/g, '') })}
              className="text-gray-800 px-3 py-2 w-full rounded-lg bg-white/90 border border-gray-300 focus:ring-2 focus:ring-yellow-400"
              min={1}
              step="any"
              required
            />
          </div>

          <div className="col-span-1 md:col-span-1">
            <label className="block mb-1 text-sm font-medium">Token / Unit</label>
            <input
              type="number"
              placeholder="Rate"
              value={newOffer.token_per_unit}
              onChange={(e) =>
                setNewOffer({ ...newOffer, token_per_unit: e.target.value.replace(/[^0-9.]/g, '') })
              }
              className="text-gray-800 px-3 py-2 w-full rounded-lg bg-white/90 border border-gray-300 focus:ring-2 focus:ring-yellow-400"
              min={1}
              step="any"
              required
            />
          </div>
          
          <div className="col-span-2 md:col-span-1 text-center bg-green-800/50 p-2 rounded-lg">
            <p className="text-xs text-green-200">Total {isBuy ? 'Cost' : 'Earning'}</p>
            <p className="font-bold text-lg text-yellow-300">{totalTokens.toFixed(2)} Tokens</p>
          </div>

          <button
            type="submit"
            disabled={!newOffer.units || !newOffer.token_per_unit || loading}
            className="col-span-2 md:col-span-1 bg-yellow-400 hover:bg-yellow-500 text-gray-900 font-bold px-4 py-2 rounded-lg transition-all duration-200 shadow-md hover:shadow-lg disabled:opacity-50 disabled:cursor-not-allowed"
          >
            Create Offer
          </button>
        </form>
      </div>

      {/* OFFERS LIST */}
      <h2 className="font-bold text-2xl mb-4 text-center text-white/90">Active Market Offers</h2>
      {loading ? (
        <p className="text-center text-xl p-8 bg-green-700/50 rounded-lg">
            <ClockIcon className="w-6 h-6 inline-block animate-spin mr-2"/>
            Loading offers...
        </p>
      ) : offers.length === 0 ? (
        <p className="text-center text-xl p-8 bg-green-700/50 rounded-lg">No active offers near your transformer area.</p>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-5">
          {offers.map((offer) => (
            <div
              key={offer._id}
              className={`p-5 rounded-xl shadow-xl transition-all duration-300 transform hover:scale-[1.02] 
                ${offer.offer_type === "sell" 
                    ? "bg-blue-800/70 border-l-4 border-blue-300" 
                    : "bg-red-800/70 border-l-4 border-red-300"
                }`}
            >
              <div className="flex justify-between items-start mb-3 border-b border-white/20 pb-2">
                <h3 className="font-extrabold text-lg flex items-center gap-2">
                  <ArrowRightIcon className="w-5 h-5 text-yellow-400" />
                  {offer.offer_type.toUpperCase()} OFFER
                </h3>
                <span className={`px-3 py-1 text-xs rounded-full font-bold shadow-md ${
                    offer.status === 'open' ? 'bg-green-500' : 'bg-gray-500'
                }`}>
                  {offer.status.toUpperCase()}
                </span>
              </div>

              <div className="space-y-1 text-sm">
                <p><b>ID:</b> {offer.offer_id}</p>
                <p><b>Units:</b> <span className="font-semibold text-lg">{offer.units}</span> kWh</p>
                <p><b>Rate:</b> {offer.token_per_unit} Tokens/unit</p>
                <p className="pt-2 font-bold text-yellow-300">
                  Total Value: {offer.total_tokens ? offer.total_tokens.toFixed(2) : (offer.units * offer.token_per_unit).toFixed(2)} Tokens
                </p>

                <p className="flex items-center gap-1 text-xs text-white/70 pt-2">
                  <UserIcon className="w-4 h-4" />
                  Creator: {offer.creator_id}
                </p>
                <p className="flex items-center gap-1 text-xs text-white/70">
                  <ClockIcon className="w-4 h-4" />
                  Created: {new Date(offer.created_at).toLocaleString()}
                </p>
              </div>
              
              {/* Add a button for interaction (e.g., Negotiate/Accept) if implementing that feature */}
            </div>
          ))}
        </div>
      )}

      <NavigationBar active="Trade" />
    </div>
  );
}
