import React, { useState, useEffect } from "react";
import { BoltIcon, ClockIcon, UserIcon } from "@heroicons/react/24/outline";
import NavigationBar from "../components/navbar.jsx";
import { getCurrentOffers, createOffer } from "../api.js"; // import helpers

export default function TradePage() {
  const [offers, setOffers] = useState([]);
  const [newOffer, setNewOffer] = useState({
    offer_type: "sell",
    units: 0,
    token_per_unit: 0,
  });
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchOffers();
  }, []);

  // Fetch current offers
  const fetchOffers = async () => {
    try {
      const res = await getCurrentOffers();
      if (res.success) setOffers(res.data || []);
      else alert(res.message || "Failed to fetch offers");
    } catch (err) {
      console.error(err);
      alert("Error fetching offers");
    }
  };

  // Create new offer
  const handleCreateOffer = async (e) => {
    e.preventDefault();
    if (!newOffer.units || !newOffer.token_per_unit) return;

    setLoading(true);
    try {
      const res = await createOffer(newOffer);
      if (res.success) {
        alert("Offer created successfully!");
        fetchOffers();
        setNewOffer({ offer_type: "sell", units: 0, token_per_unit: 0 });
      } else {
        alert(res.message || "Failed to create offer");
      }
    } catch (err) {
      console.error(err);
      alert("Error creating offer");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-green-600 to-green-800 text-white p-6 pb-24">
      <h1 className="text-3xl font-bold text-center mb-6">âš¡ Energy Trading</h1>

      {/* New Offer Form */}
      <div className="bg-green-700 rounded-lg p-4 mb-6 shadow-md">
        <h2 className="font-semibold mb-2">Create New Offer</h2>
        <form
          className="flex flex-wrap gap-4 items-end"
          onSubmit={handleCreateOffer}
        >
          <div>
            <label className="block mb-1">Type</label>
            <select
              value={newOffer.offer_type}
              onChange={(e) =>
                setNewOffer({ ...newOffer, offer_type: e.target.value })
              }
              className="text-black rounded px-2 py-1"
            >
              <option value="sell">Sell</option>
              <option value="buy">Buy</option>
            </select>
          </div>
          <div>
            <label className="block mb-1">Units</label>
            <input
              type="number"
              value={newOffer.units}
              onChange={(e) =>
                setNewOffer({ ...newOffer, units: Number(e.target.value) })
              }
              className="text-black rounded px-2 py-1"
              required
              min={1}
            />
          </div>
          <div>
            <label className="block mb-1">Token/Unit</label>
            <input
              type="number"
              value={newOffer.token_per_unit}
              onChange={(e) =>
                setNewOffer({
                  ...newOffer,
                  token_per_unit: Number(e.target.value),
                })
              }
              className="text-black rounded px-2 py-1"
              required
              min={1}
            />
          </div>
          <button
            type="submit"
            disabled={loading || newOffer.units <= 0 || newOffer.token_per_unit <= 0}
            className="bg-yellow-500 hover:bg-yellow-600 text-black px-4 py-2 rounded disabled:opacity-50"
          >
            {loading ? "Creating..." : "Create"}
          </button>
        </form>
      </div>

      {/* Offers List */}
      <div className="grid gap-4">
        {offers.map((offer) => (
          <div
            key={offer._id}
            className={`p-4 rounded-lg shadow-md ${
              offer.status === "cancelled"
                ? "bg-red-500 text-white"
                : "bg-green-500 hover:bg-green-600 text-white"
            }`}
          >
            <div className="flex justify-between items-center mb-2">
              <h3 className="font-bold text-lg flex items-center gap-2">
                <BoltIcon className="w-5 h-5" />
                {offer.offer_id}
              </h3>
              <span className="px-2 py-1 rounded-full bg-red-700 text-sm">
                {offer.status}
              </span>
            </div>
            <p>
              <strong>Type:</strong> {offer.offer_type}
            </p>
            <p>
              <strong>Units:</strong> {offer.units} kWh
            </p>
            <p>
              <strong>Rate/Unit:</strong> {offer.token_per_unit} Tokens
            </p>
            <p>
              <strong>Total Tokens:</strong>{" "}
              {offer.total_tokens ?? offer.units * offer.token_per_unit}
            </p>
            <p className="flex items-center gap-1">
              <UserIcon className="w-4 h-4" /> Created By: {offer.creator_id}
            </p>
            <div className="flex gap-4 mt-2 text-sm">
              <p className="flex items-center gap-1">
                <ClockIcon className="w-4 h-4" />
                Created: {new Date(offer.created_at).toLocaleString()}
              </p>
              {offer.completed_at && (
                <p className="flex items-center gap-1">
                  <ClockIcon className="w-4 h-4" />
                  Completed: {new Date(offer.completed_at).toLocaleString()}
                </p>
              )}
            </div>
          </div>
        ))}
      </div>

      {/* Navigation Bar */}
      <NavigationBar active="Trade" />
    </div>
  );
}
