import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { WalletIcon, CubeIcon, BoltIcon, UserIcon, CogIcon, KeyIcon } from "@heroicons/react/24/outline";

// Navigation Bar
const NavigationBar = ({ active }) => {
  const navigate = useNavigate();
  const NavItem = ({ name, icon, route }) => (
    <button
      aria-label={name}
      className={`flex flex-col items-center justify-center p-2 text-xs font-medium transition-colors ${
        active === name
          ? "text-emerald-600 border-t-2 border-emerald-600"
          : "text-gray-500 hover:text-emerald-500"
      }`}
      onClick={() => navigate(route)}
    >
      {icon}
      {name}
    </button>
  );

  return (
    <div className="fixed inset-x-0 bottom-0 bg-white border-t border-gray-200 shadow-2xl z-50">
      <div className="max-w-4xl mx-auto flex justify-around">
        <NavItem name="Dashboard" icon={<BoltIcon className="w-5 h-5" />} route="/dashboard" />
        <NavItem name="Trade" icon={<CubeIcon className="w-5 h-5" />} route="/trade" />
        <NavItem name="History" icon={<WalletIcon className="w-5 h-5" />} route="/history" />
        <NavItem name="Profile" icon={<UserIcon className="w-5 h-5" />} route="/profile" />
      </div>
    </div>
  );
};

// Skeleton Loader Component
const SkeletonCard = ({ height = "h-24" }) => (
  <div className={`animate-pulse bg-gray-200 rounded-xl ${height} w-full`} />
);

// Main Profile Component
export default function Profile() {
  const navigate = useNavigate();
  const [isAuthenticated, setIsAuthenticated] = useState(true); // Mock auth
  const [profile, setProfile] = useState(null);
  const [balances, setBalances] = useState(null);
  const [technical, setTechnical] = useState(null);
  const [errors, setErrors] = useState({ profile: "", balances: "", technical: "" });
  const [loading, setLoading] = useState(true);
  const [currentPassword, setCurrentPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [passwordMsg, setPasswordMsg] = useState("");

  const token = localStorage.getItem("token") || "mock-token";

  // Mock fetch function
  const mockFetch = async (endpoint, options = {}) => {
    await new Promise((r) => setTimeout(r, 600));
    if (!token) throw new Error("Unauthorized");

    if (endpoint.includes("profile/balances")) {
      return {
        json: () => ({
          success: true,
          data: {
            wallet_balance: "$1,250.00",
            token_balance: "5,800 P2P",
            energy_balance: "450 kWh",
            reserved_token_balance: "1,200 P2P",
            reserved_energy_balance: "150 kWh",
          },
        }),
      };
    } else if (endpoint.includes("profile/technical")) {
      return {
        json: () => ({
          success: true,
          data: {
            meterId: "MT-3029-A",
            transformerId: "TX-445-Z",
            totalEnergySold: "1,560 kWh",
            totalEnergyBought: "980 kWh",
          },
        }),
      };
    } else if (endpoint.includes("profile")) {
      return {
        json: () => ({
          success: true,
          data: {
            name: "A. G. Customer",
            email: "a.g.customer@grid.com",
            userId: "NODE-X87F32",
          },
        }),
      };
    } else if (endpoint.includes("change-password")) {
      const body = JSON.parse(options.body);
      if (body.currentPassword === "wrong" || body.newPassword.length < 6)
        return { json: () => ({ message: "Password must be at least 6 characters." }) };
      return { json: () => ({ message: "Secure Key updated successfully!" }) };
    }
    return { json: () => ({ success: false, message: "Not Found" }) };
  };

  // Fetch data
  const fetchData = async () => {
    if (!isAuthenticated) return;
    setLoading(true);

    try {
      const resProfile = await mockFetch("/api/auth/profile", { headers: { Authorization: `Bearer ${token}` } });
      const dataProfile = await resProfile.json();
      if (dataProfile.success) setProfile(dataProfile.data);
      else setErrors((prev) => ({ ...prev, profile: dataProfile.message }));
    } catch {
      setErrors((prev) => ({ ...prev, profile: "Failed to fetch profile" }));
    }

    try {
      const resBalances = await mockFetch("/api/auth/profile/balances", { headers: { Authorization: `Bearer ${token}` } });
      const dataBalances = await resBalances.json();
      if (dataBalances.success) setBalances(dataBalances.data);
      else setErrors((prev) => ({ ...prev, balances: dataBalances.message }));
    } catch {
      setErrors((prev) => ({ ...prev, balances: "Failed to fetch balances" }));
    }

    try {
      const resTech = await mockFetch("/api/auth/profile/technical", { headers: { Authorization: `Bearer ${token}` } });
      const dataTech = await resTech.json();
      if (dataTech.success) setTechnical(dataTech.data);
      else setErrors((prev) => ({ ...prev, technical: dataTech.message }));
    } catch {
      setErrors((prev) => ({ ...prev, technical: "Failed to fetch technical info" }));
    }

    setLoading(false);
  };

  useEffect(() => {
    fetchData();
  }, [isAuthenticated]);

  // Change password handler
  const handleChangePassword = async () => {
    if (!currentPassword || !newPassword) {
      setPasswordMsg("Please fill both fields.");
      return;
    }
    setPasswordMsg("Processing...");
    try {
      const res = await mockFetch("/api/auth/change-password", {
        method: "POST",
        headers: { "Content-Type": "application/json", Authorization: `Bearer ${token}` },
        body: JSON.stringify({ currentPassword, newPassword }),
      });
      const data = await res.json();
      setPasswordMsg(data.message || "Failed to update Secure Key");
      setCurrentPassword("");
      setNewPassword("");
    } catch {
      setPasswordMsg("Network error. Try again.");
    }
  };

  if (!isAuthenticated)
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-100">
        <div className="p-8 bg-white rounded-xl shadow-lg text-center">
          <p className="text-xl font-semibold text-red-600 mb-4">Session Expired</p>
          <button
            onClick={() => setIsAuthenticated(true)}
            className="bg-emerald-500 text-white p-3 rounded hover:bg-emerald-600 transition"
          >
            Go to Login Screen
          </button>
        </div>
      </div>
    );

  if (loading)
    return (
      <div className="p-6 text-center text-xl font-semibold text-gray-700 min-h-screen bg-gray-50 flex items-center justify-center">
        Loading P2P Node Data...
      </div>
    );

  return (
    <div className="p-6 pb-20 max-w-4xl mx-auto space-y-6 bg-gray-50 min-h-screen">
      <h1 className="text-3xl font-bold text-gray-800 border-b pb-2 mb-4">P2P Node Profile</h1>

      {/* Personal & Identity */}
      <div className="bg-gradient-to-r from-emerald-600 to-green-700 text-white p-6 rounded-xl shadow-lg space-y-2">
        <h2 className="text-xl font-bold flex items-center space-x-2">
          <UserIcon className="w-6 h-6" /> <span>Personal & Identity</span>
        </h2>
        {errors.profile && <p className="text-red-200 mt-1">{errors.profile}</p>}
        <p><strong>Name:</strong> {profile?.name || "Grid Participant"}</p>
        <p><strong>Email:</strong> {profile?.email || "No Email Registered"}</p>
        <p>
          <strong>User ID:</strong>{" "}
          <span className="font-mono bg-white bg-opacity-20 p-1 rounded ml-1 text-xs">{profile?.userId || "N/A"}</span>
        </p>
      </div>

      {/* Current Balances */}
      <h2 className="text-xl font-bold text-gray-800 pt-2">Current Balances</h2>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        <div className="bg-white p-6 rounded-xl shadow hover:shadow-xl transition-transform duration-300 hover:scale-105 border-l-4 border-indigo-500">
          <div className="flex items-center space-x-2 mb-2">
            <WalletIcon className="w-6 h-6 text-indigo-500" />
            <h3 className="font-semibold text-lg text-gray-700">Wallet Balance</h3>
          </div>
          {errors.balances && <p className="text-red-600 text-sm">{errors.balances}</p>}
          <p className="text-2xl font-extrabold text-indigo-700">{balances?.wallet_balance ?? "-"}</p>
        </div>
        <div className="bg-white p-6 rounded-xl shadow hover:shadow-xl transition-transform duration-300 hover:scale-105 border-l-4 border-teal-500">
          <div className="flex items-center space-x-2 mb-2">
            <CubeIcon className="w-6 h-6 text-teal-500" />
            <h3 className="font-semibold text-lg text-gray-700">Token Balance</h3>
          </div>
          <p className="text-2xl font-extrabold text-teal-700">{balances?.token_balance ?? "-"}</p>
        </div>
        <div className="bg-white p-6 rounded-xl shadow hover:shadow-xl transition-transform duration-300 hover:scale-105 border-l-4 border-amber-500">
          <div className="flex items-center space-x-2 mb-2">
            <BoltIcon className="w-6 h-6 text-amber-500" />
            <h3 className="font-semibold text-lg text-gray-700">Energy Balance</h3>
          </div>
          <p className="text-2xl font-extrabold text-amber-700">{balances?.energy_balance ?? "-"}</p>
        </div>
      </div>

      {/* Reserved Balances */}
      <h2 className="text-xl font-bold text-gray-800 pt-2">Reserved Balances (In Trade)</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        <div className="bg-white p-6 rounded-xl shadow border-l-4 border-teal-400">
          <h3 className="font-semibold text-md text-gray-700">Reserved Token Balance</h3>
          <p className="text-xl font-bold text-teal-600 mt-1">{balances?.reserved_token_balance ?? "-"}</p>
          <p className="text-xs text-gray-500">Tokens locked for active sell orders.</p>
        </div>
        <div className="bg-white p-6 rounded-xl shadow border-l-4 border-amber-400">
          <h3 className="font-semibold text-md text-gray-700">Reserved Energy Balance</h3>
          <p className="text-xl font-bold text-amber-600 mt-1">{balances?.reserved_energy_balance ?? "-"}</p>
          <p className="text-xs text-gray-500">Energy allocated for delivery contracts.</p>
        </div>
      </div>

      {/* Technical Info */}
      <div className="bg-white p-6 rounded-xl shadow">
        <div className="flex items-center space-x-2 mb-4 border-b pb-2">
          <CogIcon className="w-6 h-6 text-gray-700" />
          <h3 className="font-semibold text-xl text-gray-800">Node Technical Information & History</h3>
        </div>
        {errors.technical && <p className="text-red-600 mb-2">{errors.technical}</p>}
        <div className="grid grid-cols-2 gap-4 text-gray-700">
          <div>
            <p className="mb-2">
              <strong>Meter ID:</strong>{" "}
              <span className="font-mono bg-gray-100 p-1 rounded text-sm">{technical?.meterId ?? "N/A"}</span>
            </p>
            <p>
              <strong>Transformer ID:</strong>{" "}
              <span className="font-mono bg-gray-100 p-1 rounded text-sm">{technical?.transformerId ?? "N/A"}</span>
            </p>
          </div>
          <div>
            <p className="mb-2">
              <strong>Total Energy Sold:</strong>{" "}
              <span className="font-bold text-emerald-600">{technical?.totalEnergySold ?? "0 kWh"}</span>
            </p>
            <p>
              <strong>Total Energy Bought:</strong>{" "}
              <span className="font-bold text-indigo-600">{technical?.totalEnergyBought ?? "0 kWh"}</span>
            </p>
          </div>
        </div>
      </div>

      {/* Change Password */}
      <div className="bg-white p-6 rounded-xl shadow">
        <div className="flex items-center space-x-2 mb-4 border-b pb-2">
          <KeyIcon className="w-6 h-6 text-red-500" />
          <h3 className="font-semibold text-xl text-gray-800">Update Secure Key (Change Password)</h3>
        </div>
        {passwordMsg && (
          <p className={`text-sm mb-2 ${passwordMsg.toLowerCase().includes("success") ? "text-green-600" : "text-red-600"}`}>
            {passwordMsg}
          </p>
        )}
        <input
          type="password"
          placeholder="Current Secure Key"
          className="border p-3 w-full rounded-lg mb-3 focus:outline-none focus:ring-2 focus:ring-emerald-400 transition"
          value={currentPassword}
          onChange={(e) => setCurrentPassword(e.target.value)}
        />
        <input
          type="password"
          placeholder="New Secure Key"
          className="border p-3 w-full rounded-lg mb-4 focus:outline-none focus:ring-2 focus:ring-emerald-400 transition"
          value={newPassword}
          onChange={(e) => setNewPassword(e.target.value)}
        />
        <button
          className="bg-emerald-600 text-white p-3 rounded-lg w-full hover:bg-emerald-700 transition font-semibold shadow-md"
          onClick={handleChangePassword}
          disabled={passwordMsg === "Processing..."}
        >
          {passwordMsg === "Processing..." ? "Updating..." : "Change Secure Key"}
        </button>
      </div>

      {/* Navigation Bar */}
      <NavigationBar active="Profile" />
    </div>
  );
}
