import React, { useEffect, useState } from "react";
import {
  WalletIcon,
  CubeIcon,
  BoltIcon,
  UserIcon,
  CogIcon,
  KeyIcon,
  ArrowRightOnRectangleIcon,
} from "@heroicons/react/24/outline";
import { io } from "socket.io-client";

const socket = io("http://localhost:5001", {
  auth: { token: localStorage.getItem("token") },
});

const NavigationBar = ({ active }) => {
  const NavItem = ({ name, icon, route }) => (
    <button
      aria-label={name}
      className={`flex flex-col items-center space-y-1 p-2 text-gray-600 ${
        active === name ? "text-blue-500" : ""
      }`}
      onClick={() => (window.location.href = route)}
    >
      {icon}
      <span className="text-sm">{name}</span>
    </button>
  );

  return (
    <nav className="fixed bottom-0 left-0 w-full bg-white shadow-lg p-2 flex justify-around">
      <NavItem name="Wallet" icon={<WalletIcon className="w-6 h-6" />} route="/wallet" />
      <NavItem name="Offers" icon={<CubeIcon className="w-6 h-6" />} route="/offers" />
      <NavItem name="Energy" icon={<BoltIcon className="w-6 h-6" />} route="/energy" />
      <NavItem name="Profile" icon={<UserIcon className="w-6 h-6" />} route="/profile" />
    </nav>
  );
};

const Profile = () => {
  const [userData, setUserData] = useState(null);
  const token = localStorage.getItem("token");

  // -------- Fetch Profile --------
  const fetchProfile = async () => {
    try {
      const res = await fetch("http://localhost:5001/api/user/profile", {
        headers: { Authorization: `Bearer ${token}` },
      });

      const data = await res.json();
      if (data.success) {
        setUserData(data.user);
      }
    } catch (error) {
      console.error("Profile fetch error:", error);
    }
  };

  useEffect(() => {
    fetchProfile();

    // Listen for real-time meter readings
    socket.on("new_reading", () => {
      console.log("ðŸ”„ New reading received â†’ refreshing profile...");
      fetchProfile(); // refresh energy balance + last readings
    });

    return () => socket.off("new_reading");
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token");
    window.location.reload();
  };

  if (!userData) {
    return <div className="p-6 text-center">Loading profile...</div>;
  }

  return (
    <div className="p-6 space-y-6 pb-20">
      <h1 className="text-2xl font-bold text-gray-800">Profile</h1>

      {/* User Details */}
      <div className="bg-white shadow-lg rounded-xl p-6 space-y-3">
        <p className="text-gray-700">
          <strong>Name:</strong> {userData.name}
        </p>
        <p className="text-gray-700">
          <strong>Email:</strong> {userData.email}
        </p>
        <p className="text-gray-700">
          <strong>Meter ID:</strong> {userData.meter_id}
        </p>
      </div>

      {/* Energy Section */}
      <div className="bg-white shadow-lg rounded-xl p-6 space-y-3">
        <h2 className="text-xl font-semibold text-gray-800">Energy Details</h2>

        <p className="text-gray-700">
          <strong>Last Import Reading:</strong> {userData.last_import_reading} kWh
        </p>

        <p className="text-gray-700">
          <strong>Last Export Reading:</strong> {userData.last_export_reading} kWh
        </p>

        <p className="text-gray-700">
          <strong>Total Bought:</strong> {userData.total_energy_bought} kWh
        </p>

        <p className="text-gray-700">
          <strong>Total Sold:</strong> {userData.total_energy_sold} kWh
        </p>

        <p className="text-gray-800 text-lg font-bold">
          âš¡ Energy Balance: {userData.energy_balance} kWh
        </p>
      </div>

      {/* Logout */}
      <div>
        <button
          onClick={handleLogout}
          className="flex items-center space-x-1 bg-red-500 text-white px-4 py-2 rounded-lg hover:bg-red-600 transition shadow-md"
        >
          <ArrowRightOnRectangleIcon className="w-5 h-5" />
          <span>Logout</span>
        </button>
      </div>

      <NavigationBar active="Profile" />
    </div>
  );
};

export default Profile;
