import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

export default function Profile() {
  const navigate = useNavigate();

  const [profile, setProfile] = useState(null);
  const [balances, setBalances] = useState(null);
  const [technical, setTechnical] = useState(null);
  const [errors, setErrors] = useState({ profile: "", balances: "", technical: "" });
  const [loading, setLoading] = useState(true);

  const [currentPassword, setCurrentPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [passwordMsg, setPasswordMsg] = useState("");

  const token = localStorage.getItem("token");

  // Redirect if no token
  useEffect(() => {
    if (!token) navigate("/login");
  }, [token, navigate]);

  const fetchData = async () => {
    if (!token) return;

    setLoading(true);

    // Profile
    try {
      const res = await fetch("http://localhost:5001/api/auth/profile", {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      if (data.success) setProfile(data.data);
      else setErrors((prev) => ({ ...prev, profile: data.message || "Failed to fetch profile" }));
    } catch (err) {
      setErrors((prev) => ({ ...prev, profile: "Failed to fetch profile" }));
      console.error("Profile error:", err);
    }

    // Balances
    try {
      const res = await fetch("http://localhost:5001/api/auth/profile/balances", {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      if (data.success) setBalances(data.data);
      else setErrors((prev) => ({ ...prev, balances: data.message || "Failed to fetch balances" }));
    } catch (err) {
      setErrors((prev) => ({ ...prev, balances: "Failed to fetch balances" }));
      console.error("Balances error:", err);
    }

    // Technical info
    try {
      const res = await fetch("http://localhost:5001/api/auth/profile/technical", {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      if (data.success) setTechnical(data.data);
      else setErrors((prev) => ({ ...prev, technical: data.message || "Failed to fetch technical info" }));
    } catch (err) {
      setErrors((prev) => ({ ...prev, technical: "Failed to fetch technical info" }));
      console.error("Technical error:", err);
    }

    setLoading(false);
  };

  useEffect(() => {
    fetchData();
  }, []);

  // Change password
  const handleChangePassword = async () => {
    if (!currentPassword || !newPassword) {
      setPasswordMsg("Please fill both fields.");
      return;
    }
    try {
      const res = await fetch("http://localhost:5001/api/auth/change-password", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ currentPassword, newPassword }),
      });
      const data = await res.json();
      setPasswordMsg(data.message || "Password change failed");
      setCurrentPassword("");
      setNewPassword("");
    } catch (err) {
      console.error("Change password error:", err);
      setPasswordMsg("Failed to change password.");
    }
  };

  return (
    <div className="p-6 max-w-3xl mx-auto space-y-6">
      {loading && <div className="p-6 text-center">Loading...</div>}

      {/* Profile */}
      <div className="bg-white p-6 rounded shadow">
        <h1 className="text-2xl font-bold mb-4">Profile</h1>
        {errors.profile && <p className="text-red-600 mb-2">{errors.profile}</p>}
        <p><strong>Name:</strong> {profile?.name || "-"}</p>
        <p><strong>Email:</strong> {profile?.email || "-"}</p>
      </div>

      {/* Balances */}
      <div className="bg-white p-6 rounded shadow">
        <h2 className="text-xl font-semibold mb-4">Balances</h2>
        {errors.balances && <p className="text-red-600 mb-2">{errors.balances}</p>}
        <p><strong>Wallet Balance:</strong> {balances?.wallet_balance ?? "-"}</p>
        <p><strong>Token Balance:</strong> {balances?.token_balance ?? "-"}</p>
        <p><strong>Energy Balance:</strong> {balances?.energy_balance ?? "-"}</p>
      </div>

      {/* Technical Info */}
      <div className="bg-white p-6 rounded shadow">
        <h2 className="text-xl font-semibold mb-4">Technical Info</h2>
        {errors.technical && <p className="text-red-600 mb-2">{errors.technical}</p>}
        <p><strong>Meter ID:</strong> {technical?.meterId || "-"}</p>
        <p><strong>Transformer ID:</strong> {technical?.transformerId || "-"}</p>
      </div>

      {/* Change Password */}
      <div className="bg-white p-6 rounded shadow">
        <h2 className="text-xl font-semibold mb-4">Change Password</h2>
        {passwordMsg && <p className="text-sm text-red-600 mb-2">{passwordMsg}</p>}
        <input
          type="password"
          placeholder="Current Password"
          className="border p-3 w-full rounded mb-2"
          value={currentPassword}
          onChange={(e) => setCurrentPassword(e.target.value)}
        />
        <input
          type="password"
          placeholder="New Password"
          className="border p-3 w-full rounded mb-2"
          value={newPassword}
          onChange={(e) => setNewPassword(e.target.value)}
        />
        <button
          className="bg-blue-600 text-white p-3 rounded w-full hover:bg-blue-700 transition"
          onClick={handleChangePassword}
        >
          Change Password
        </button>
      </div>
    </div>
  );
}
