import React, { useState, useEffect } from "react";
import { BoltIcon, ClockIcon } from "@heroicons/react/24/outline";

// --- Navigation Bar ---
const NavigationBar = ({ active }) => {
  const navItems = ["Dashboard", "Trade", "History", "Profile"];

  return (
    <div className="fixed bottom-0 left-0 right-0 bg-slate-900/90 backdrop-blur-md shadow-2xl border-t border-emerald-500/50 flex justify-around p-3 z-50">
      {navItems.map((item) => (
        <div
          key={item}
          className={`text-center transition duration-300 p-2 rounded-lg cursor-pointer text-sm font-semibold 
            ${
              item === active
                ? "text-yellow-300 bg-emerald-700/50"
                : "text-gray-400 hover:text-emerald-300"
            }`}
        >
          {item}
        </div>
      ))}
    </div>
  );
};

export default function History() {
  const [history, setHistory] = useState([]);
  const [loading, setLoading] = useState(true);
  const [serverError, setServerError] = useState(false);

  const fetchHistory = async () => {
    try {
      const token = localStorage.getItem("token");

      const response = await fetch(
        "http://localhost:5001/api/offers/closed/30days",
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      if (!response.ok) {
        setServerError(true);
        setLoading(false);
        return;
      }

      const result = await response.json();
      setHistory(result.data || []);
      setLoading(false);
      setServerError(false);
    } catch (err) {
      setServerError(true);
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchHistory();
  }, []);

  return (
    <div className="min-h-screen pb-24 p-6 bg-gradient-to-b from-green-700 via-emerald-600 to-slate-900">
      
      <h1 className="text-4xl font-extrabold text-white text-center drop-shadow-lg mb-6 tracking-wide">
        ⚡ Energy Trading History
      </h1>

      {serverError && (
        <div className="text-center text-yellow-300 text-xl mt-20 bg-white/10 p-4 rounded-xl backdrop-blur border border-yellow-500/40">
          ⚠️ Connecting to energy server...
        </div>
      )}

      {!serverError && loading && (
        <div className="text-center text-white text-xl mt-20 bg-white/10 p-4 rounded-xl backdrop-blur">
          Fetching energy trades...
        </div>
      )}

      {!loading && !serverError && history.length === 0 && (
        <div className="text-center text-white text-xl mt-20 bg-white/10 p-4 rounded-xl backdrop-blur border border-white/30">
          No energy trades recorded yet
        </div>
      )}

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 mt-4">
        {!loading &&
          !serverError &&
          history.length > 0 &&
          history.map((trade) => (
            <div
              key={trade._id}
              className="rounded-3xl p-6 bg-white/10 backdrop-blur-lg shadow-xl border border-emerald-300/40 hover:shadow-green-400/40 transition hover:scale-[1.03] text-white relative overflow-hidden"
            >
              <div className="absolute inset-x-0 top-0 h-1 bg-gradient-to-r from-yellow-300 to-green-400"></div>

              <div className="flex justify-between items-center mb-3">
                <h2 className="font-bold text-lg flex items-center gap-2">
                  <BoltIcon className="w-6 h-6 text-yellow-300" />
                  {trade.offer_id}
                </h2>

                <span
                  className={`px-3 py-1 rounded-full text-sm font-semibold capitalize ${
                    trade.status === "cancelled"
                      ? "bg-red-600"
                      : trade.status === "completed"
                      ? "bg-green-600"
                      : "bg-gray-700"
                  }`}
                >
                  {trade.status}
                </span>
              </div>

              <div className="space-y-1">
                <p><strong className="text-emerald-300">Type:</strong> {trade.offer_type}</p>
                <p><strong className="text-emerald-300">Units:</strong> {trade.units} kWh</p>
                <p><strong className="text-emerald-300">Rate/Unit:</strong> {trade.token_per_unit} Tokens</p>
                <p><strong className="text-emerald-300">Total Tokens:</strong> {trade.total_tokens}</p>
              </div>

              <div className="mt-4 text-sm opacity-80 space-y-1">
                <div className="flex items-center gap-2">
                  <ClockIcon className="w-5 h-5 text-green-300" />
                  Created: {new Date(trade.created_at).toLocaleString()}
                </div>

                <div className="flex items-center gap-2">
                  <ClockIcon className="w-5 h-5 text-yellow-300" />
                  Completed:{" "}
                  {trade.completed_at
                    ? new Date(trade.completed_at).toLocaleString()
                    : "-"}
                </div>
              </div>
            </div>
          ))}
      </div>

      <NavigationBar active="History" />
    </div>
  );
}
