import React, { useEffect, useState } from "react";
import { io } from "socket.io-client";
import {
  BoltIcon,
  PlusCircleIcon,
  CurrencyRupeeIcon,
  ArrowPathIcon,
} from "@heroicons/react/24/outline";

import NavigationBar from "../components/navbar.jsx";

export default function Trade() {
  const token = localStorage.getItem("token");

  const [socket, setSocket] = useState(null);
  const [myOffers, setMyOffers] = useState([]);
  const [marketOffers, setMarketOffers] = useState([]);
  const [units, setUnits] = useState("");
  const [tokenPerUnit, setTokenPerUnit] = useState("");
  const [msg, setMsg] = useState("");

  // -----------------------------
  // FETCH MY ACTIVE OFFERS
  // -----------------------------
  const fetchMyOffers = async () => {
    const res = await fetch("http://localhost:5001/api/offers/my-active", {
      headers: { Authorization: `Bearer ${token}` },
    });

    const data = await res.json();
    if (data.success) setMyOffers(data.data);
  };

  // -----------------------------
  // FETCH MARKET OFFERS
  // -----------------------------
  const fetchMarketOffers = async () => {
    const res = await fetch("http://localhost:5001/api/offers/market", {
      headers: { Authorization: `Bearer ${token}` },
    });

    const data = await res.json();
    if (data.success) setMarketOffers(data.data);
  };

  // -----------------------------
  // SOCKET.IO INITIALIZATION
  // -----------------------------
  useEffect(() => {
    if (!token) return;

    const s = io("http://localhost:5001", {
      auth: { token },
      reconnectionAttempts: 3,
    });

    setSocket(s);

    s.on("connect", () => console.log("TRADE connected:", s.id));
    s.on("disconnect", () => console.log("TRADE disconnected"));

    s.on("offer_update", () => {
      fetchMyOffers();
      fetchMarketOffers();
    });

    return () => s.disconnect();
  }, [token]);

  // -----------------------------
  // FETCH INITIAL DATA
  // -----------------------------
  useEffect(() => {
    fetchMyOffers();
    fetchMarketOffers();
  }, []);

  // -----------------------------
  // CREATE NEW OFFER
  // -----------------------------
  const handleCreateOffer = async () => {
    if (!units || !tokenPerUnit) {
      setMsg("Fill all fields.");
      return;
    }

    const res = await fetch("http://localhost:5001/api/offers/create", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({
        offer_type: "sell",
        units: Number(units),
        token_per_unit: Number(tokenPerUnit),
      }),
    });

    const data = await res.json();

    if (data.success) {
      setMsg("Offer Created!");
      setUnits("");
      setTokenPerUnit("");
      fetchMyOffers();
      fetchMarketOffers();
    } else {
      setMsg(data.message || "Error");
    }

    setTimeout(() => setMsg(""), 2000);
  };

  return (
    <div className="p-6 pb-24 bg-gray-50 min-h-screen max-w-4xl mx-auto space-y-6">
      <h1 className="text-3xl font-bold text-gray-800 border-b pb-2 flex items-center gap-2">
        <ArrowPathIcon className="w-7 h-7 text-emerald-600" />
        Trade Energy
      </h1>

      {/* CREATE OFFER BOX */}
      <div className="bg-white p-6 rounded-xl shadow border-l-4 border-emerald-500 space-y-4">
        <h2 className="text-xl font-bold text-emerald-700 flex items-center gap-2">
          <PlusCircleIcon className="w-6 h-6" />
          Create New Offer
        </h2>

        {msg && (
          <div className="p-2 bg-yellow-100 text-yellow-700 rounded">{msg}</div>
        )}

        <div className="grid grid-cols-2 gap-4">
          <input
            type="number"
            placeholder="Units (kWh)"
            className="border p-3 rounded-lg focus:ring-2 focus:ring-emerald-500"
            value={units}
            onChange={(e) => setUnits(e.target.value)}
          />

          <input
            type="number"
            placeholder="Token per unit"
            className="border p-3 rounded-lg focus:ring-2 focus:ring-emerald-500"
            value={tokenPerUnit}
            onChange={(e) => setTokenPerUnit(e.target.value)}
          />
        </div>

        <button
          onClick={handleCreateOffer}
          className="w-full bg-emerald-600 hover:bg-emerald-700 text-white p-3 rounded-lg font-semibold shadow"
        >
          Create Offer
        </button>
      </div>

      {/* MY ACTIVE OFFERS */}
      <div>
        <h2 className="text-xl font-bold text-gray-800 mb-2">My Active Offers</h2>

        <div className="space-y-4">
          {myOffers.length === 0 && (
            <p className="text-gray-500 text-center">No active offers.</p>
          )}

          {myOffers.map((offer) => (
            <div
              key={offer.offer_id}
              className="bg-white p-5 rounded-xl shadow border-l-4 border-indigo-500"
            >
              <p className="font-semibold text-gray-700">Offer ID: {offer.offer_id}</p>
              <p>Units: {offer.units}</p>
              <p>Token per unit: {offer.token_per_unit}</p>
              <p>Total Tokens: {offer.total_tokens}</p>
              <p className="text-indigo-600 font-bold mt-1">Status: {offer.status}</p>
            </div>
          ))}
        </div>
      </div>

      {/* MARKET OFFERS */}
      <div>
        <h2 className="text-xl font-bold text-gray-800 mb-2">Market Offers</h2>

        <div className="space-y-4">
          {marketOffers.length === 0 && (
            <p className="text-gray-500 text-center">No offers available.</p>
          )}

          {marketOffers.map((offer) => (
            <div
              key={offer.offer_id}
              className="bg-white p-5 rounded-xl shadow border-l-4 border-amber-500"
            >
              <p className="font-semibold">Creator: {offer.creator_id}</p>
              <p>Units: {offer.units}</p>
              <p>Token per unit: {offer.token_per_unit}</p>
              <p>Total: {offer.total_tokens}</p>

              <button className="mt-3 w-full bg-amber-600 hover:bg-amber-700 text-white p-2 rounded-lg font-semibold shadow">
                Buy Offer
              </button>
            </div>
          ))}
        </div>
      </div>

      <NavigationBar active="Trade" />
    </div>
  );
}
