import React, { useEffect, useState } from "react";
import { io } from "socket.io-client";

import {
  BoltIcon,
  XMarkIcon,
  CheckIcon,
  CurrencyRupeeIcon,
  ClockIcon,
  ArrowPathIcon,
} from "@heroicons/react/24/outline";

import NavigationBar from "../components/navbar.jsx";

const API = "http://localhost:5000";

// Socket instance
const socket = io(API, { autoConnect: false });

export default function TradePage() {
  const [myOffers, setMyOffers] = useState([]);
  const [marketOffers, setMarketOffers] = useState([]);
  const [loading, setLoading] = useState(true);

  const user_id = localStorage.getItem("user_id");
  const token = localStorage.getItem("token");

  // -------------------------------
  // CONNECT SOCKET ON TRADE PAGE
  // -------------------------------
  useEffect(() => {
    if (!user_id) return;

    socket.connect();
    socket.emit("register", user_id);

    socket.on("offer_created", (data) => {
      console.log("New offer received:", data.offer);
      refreshOffers();
    });

    socket.on("offer_updated", () => {
      refreshOffers();
    });

    return () => {
      socket.disconnect();
    };
  }, []);

  // -------------------------------
  // FETCH ALL OFFERS
  // -------------------------------
  const refreshOffers = async () => {
    setLoading(true);

    try {
      const [myRes, marketRes] = await Promise.all([
        fetch(`${API}/offers/myoffers/${user_id}`),
        fetch(`${API}/offers/market/${user_id}`),
      ]);

      const myData = await myRes.json();
      const marketData = await marketRes.json();

      setMyOffers(myData.data || []);
      setMarketOffers(marketData.data || []);
    } catch (e) {
      console.error("Error fetching offers:", e);
    }

    setLoading(false);
  };

  useEffect(() => {
    refreshOffers();
  }, []);

  // -------------------------------
  // ACTION HANDLERS
  // -------------------------------
  const acceptOffer = async (offerId) => {
    const res = await fetch(`${API}/offers/accept/${offerId}`, {
      method: "POST",
      headers: { Authorization: `Bearer ${token}` },
    });

    const data = await res.json();
    if (res.ok) refreshOffers();
    else alert(data.msg);
  };

  const cancelOffer = async (offerId) => {
    const res = await fetch(`${API}/offers/cancel/${offerId}`, {
      method: "POST",
      headers: { Authorization: `Bearer ${token}` },
    });

    const data = await res.json();
    if (res.ok) refreshOffers();
    else alert(data.msg);
  };

  const negotiateOffer = async (offerId) => {
    const price = prompt("Enter your new token-per-unit price:");
    if (!price || isNaN(price)) return;

    const res = await fetch(`${API}/offers/negotiate/${offerId}`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({ price }),
    });

    const data = await res.json();
    if (res.ok) refreshOffers();
    else alert(data.msg);
  };

  const acceptNegotiation = async (offerId) => {
    const res = await fetch(`${API}/offers/accept-negotiation/${offerId}`, {
      method: "POST",
      headers: { Authorization: `Bearer ${token}` },
    });

    const data = await res.json();
    if (res.ok) refreshOffers();
    else alert(data.msg);
  };

  const cancelNegotiation = async (offerId) => {
    const res = await fetch(`${API}/offers/cancel-negotiation/${offerId}`, {
      method: "POST",
      headers: { Authorization: `Bearer ${token}` },
    });

    if (res.ok) refreshOffers();
  };

  // -------------------------------
  // OFFER CARD COMPONENT
  // -------------------------------
  const OfferCard = ({ offer, mine }) => (
    <div className="p-4 bg-slate-900 border border-green-700 rounded-xl shadow-lg">
      <h3 className="text-green-300 font-bold text-xl flex items-center gap-2">
        <BoltIcon className="w-6 h-6" />
        {offer.offer_type.toUpperCase()} Offer
      </h3>

      <p className="text-gray-300 mt-2">
        Units: <span className="text-white font-bold">{offer.units}</span>
      </p>

      <p className="text-gray-300">
        Price: <span className="text-white font-bold">{offer.token_per_unit} Token/u</span>
      </p>

      <p className="text-gray-300">
        Creator:{" "}
        <span className="text-green-400 font-semibold">{offer.creator_id}</span>
      </p>

      {offer.negotiated_tokens && (
        <p className="text-yellow-300 mt-2">
          Negotiated Price: {offer.negotiated_tokens} Token/u  
          <span className="text-sm text-gray-400"> (by {offer.negotiated_by})</span>
        </p>
      )}

      {/* ACTION BUTTONS */}
      <div className="mt-4 flex gap-2 flex-wrap">
        {!mine && (
          <>
            <button
              onClick={() => acceptOffer(offer.offer_id)}
              className="bg-green-600 px-3 py-1 rounded-lg flex items-center gap-1 hover:bg-green-700"
            >
              <CheckIcon className="w-5 h-5" /> Accept
            </button>

            <button
              onClick={() => negotiateOffer(offer.offer_id)}
              className="bg-yellow-500 px-3 py-1 rounded-lg flex items-center gap-1 hover:bg-yellow-600"
            >
              <CurrencyRupeeIcon className="w-5 h-5" /> Negotiate
            </button>
          </>
        )}

        {offer.negotiated_by === user_id && mine && (
          <>
            <button
              onClick={() => acceptNegotiation(offer.offer_id)}
              className="bg-green-700 px-3 py-1 rounded-lg hover:bg-green-800"
            >
              Accept Negotiation
            </button>

            <button
              onClick={() => cancelNegotiation(offer.offer_id)}
              className="bg-red-600 px-3 py-1 rounded-lg hover:bg-red-700"
            >
              Cancel Negotiation
            </button>
          </>
        )}

        {mine && (
          <button
            onClick={() => cancelOffer(offer.offer_id)}
            className="bg-red-500 px-3 py-1 rounded-lg flex items-center gap-1 hover:bg-red-600"
          >
            <XMarkIcon className="w-5 h-5" /> Cancel Offer
          </button>
        )}
      </div>
    </div>
  );

  // -------------------------------
  // UI OUTPUT
  // -------------------------------
  return (
    <div className="min-h-screen bg-slate-950 text-white p-4 pb-24">
      <h1 className="text-3xl font-bold text-center text-green-400 mb-6">Trade</h1>

      {loading ? (
        <p className="text-center text-xl">
          <ClockIcon className="w-6 h-6 inline animate-spin mr-2" />
          Loading...
        </p>
      ) : (
        <>
          {/* MY OFFERS */}
          <h2 className="text-xl font-bold text-green-300 mt-4 mb-3">My Offers</h2>
          {myOffers.length === 0 ? (
            <p className="text-gray-400">No offers created by you.</p>
          ) : (
            <div className="grid gap-4">
              {myOffers.map((offer) => (
                <OfferCard key={offer.offer_id} offer={offer} mine={true} />
              ))}
            </div>
          )}

          {/* MARKET */}
          <h2 className="text-xl font-bold text-green-300 mt-6 mb-3">
            Market Offers (Your Transformer)
          </h2>
          {marketOffers.length === 0 ? (
            <p className="text-gray-400">No offers in your area.</p>
          ) : (
            <div className="grid gap-4">
              {marketOffers.map((offer) => (
                <OfferCard key={offer.offer_id} offer={offer} mine={false} />
              ))}
            </div>
          )}
        </>
      )}

      <NavigationBar active="Trade" />
    </div>
  );
}
