import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";
import socket from "../socket"; // GLOBAL SOCKET (Must have autoConnect: false)
import {
  BoltIcon,
  ClockIcon,
  UserIcon,
} from "@heroicons/react/24/outline";

import NavigationBar from "../components/navbar.jsx";

export default function TradePage() {
  const [offers, setOffers] = useState([]);
  const [newOffer, setNewOffer] = useState({
    offer_type: "sell",
    units: "",
    token_per_unit: "",
  });

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  const API = "http://localhost:5001";

  const authHeader = () => {
    const token = localStorage.getItem("token");
    return {
      "Content-Type": "application/json",
      Authorization: `Bearer ${token}`,
    };
  };

  // -----------------------------
  // FETCH OFFERS (Memoized for stable dependency)
  // -----------------------------
  const fetchOffers = useCallback(async () => {
    try {
      setLoading(true);
      const res = await axios.get(`${API}/api/offers/current`, {
        headers: authHeader(),
      });

      setOffers(res.data.data || []);
      setError("");
    } catch (err) {
      console.error("Fetch Offers Error:", err);
      setError("Failed to load offers. Server connection issue.");
    } finally {
      setLoading(false);
    }
  }, []);

  // -----------------------------
  // LIFECYCLE & SOCKET MANAGEMENT (Authentication Fix)
  // -----------------------------
  useEffect(() => {
    const token = localStorage.getItem("token");
    
    if (token) {
        // Authenticate and connect the socket using the token
        socket.auth = { token: token };
        socket.connect();
    } else {
        setError("Please log in to view and create offers.");
    }
    
    fetchOffers();

    // Listen for real-time update
    socket.on("offersUpdated", fetchOffers);

    return () => {
      // Cleanup: Remove listener and disconnect socket on component unmount
      socket.off("offersUpdated", fetchOffers);
      if (token && socket.connected) {
          socket.disconnect();
      }
    };
  }, [fetchOffers]);

  // -----------------------------
  // CREATE OFFER
  // -----------------------------
  const handleCreateOffer = async (e) => {
    e.preventDefault();
    onsole.log(localStorage.getItem("user_id"))
    const creator_id = localStorage.getItem("user_id");
    if (!creator_id) {
      setError("User not logged in");
      return;
    }

    if (!newOffer.units || !newOffer.token_per_unit) {
        setError("Please enter valid units and token rate.");
        return;
    }

    const offerBody = {
      creator_id,
      offer_type: newOffer.offer_type,
      units: Number(newOffer.units),
      token_per_unit: Number(newOffer.token_per_unit),
    };

    try {
      const res = await axios.post(`${API}/api/offers/create`, offerBody, {
        headers: authHeader(),
      });

      if (res.data.success) {
        setNewOffer({ offer_type: "sell", units: "", token_per_unit: "" });
        setError("");

        socket.emit("offerCreated"); 
        fetchOffers();
      } else {
        setError(res.data.message || "Failed to create offer");
      }
    } catch (err) {
      console.error("Create Offer Error:", err);
      // Retrieve the specific error message from the backend response
      const backendMessage = err.response?.data?.msg;
      if (backendMessage) {
          setError(`Failed to create offer: ${backendMessage}`);
      } else {
          setError("Failed to create offer. Check network or server status.");
      }
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-green-600 to-green-800 text-white p-6 pb-24">
      <h1 className="text-3xl font-bold text-center mb-6">⚡ Energy Trading</h1>

      {/* ERROR */}
      {error && (
        <div className="mb-4 text-center text-red-300 font-semibold">
          ⚠ {error}
        </div>
      )}

      {/* CREATE OFFER */}
      <div className="bg-green-700 rounded-lg p-4 mb-6 shadow-md">
        <h2 className="font-semibold mb-2">Create New Offer</h2>
        <form className="flex flex-wrap gap-4 items-end" onSubmit={handleCreateOffer}>
          
          <div>
            <label className="block mb-1">Type</label>
            <select
              value={newOffer.offer_type}
              onChange={(e) => setNewOffer({ ...newOffer, offer_type: e.target.value })}
              className="text-black px-2 py-1 rounded"
            >
              <option value="sell">Sell</option>
              <option value="buy">Buy</option>
            </select>
          </div>

          <div>
            <label className="block mb-1">Units</label>
            <input
              type="number"
              value={newOffer.units}
              onChange={(e) => setNewOffer({ ...newOffer, units: e.target.value })}
              className="text-black px-2 py-1 rounded"
              min={1}
              required
            />
          </div>

          <div>
            <label className="block mb-1">Token / Unit</label>
            <input
              type="number"
              value={newOffer.token_per_unit}
              onChange={(e) =>
                setNewOffer({ ...newOffer, token_per_unit: e.target.value })
              }
              className="text-black px-2 py-1 rounded"
              min={1}
              required
            />
          </div>

          <button
            type="submit"
            disabled={!newOffer.units || !newOffer.token_per_unit}
            className="bg-yellow-400 hover:bg-yellow-500 text-black px-4 py-2 rounded disabled:opacity-50"
          >
            Create
          </button>
        </form>
      </div>

      {/* OFFERS LIST */}
      {loading ? (
        <p className="text-center text-lg">⏳ Loading...</p>
      ) : offers.length === 0 ? (
        <p className="text-center text-lg">No active offers.</p>
      ) : (
        <div className="grid gap-4">
          {offers.map((offer) => (
            <div
              key={offer._id}
              className={`p-4 rounded-xl shadow-md ${
                offer.status === "cancelled"
                  ? "bg-red-500"
                  : "bg-green-500 hover:bg-green-600"
              }`}
            >
              <div className="flex justify-between items-center mb-2">
                <h3 className="font-bold text-lg flex items-center gap-2">
                  <BoltIcon className="w-5 h-5" />
                  {offer.offer_id}
                </h3>
                <span className="px-2 py-1 rounded bg-black/30 text-sm">
                  {offer.status}
                </span>
              </div>

              <p><b>Type:</b> {offer.offer_type}</p>
              <p><b>Units:</b> {offer.units} kWh</p>
              <p><b>Rate:</b> {offer.token_per_unit} Tokens/unit</p>
              <p>
                <b>Total Tokens:</b>{" "}
                {offer.total_tokens ?? (offer.units * offer.token_per_unit).toFixed(2)}
              </p>

              <p className="flex items-center gap-1 mt-2">
                <UserIcon className="w-4 h-4" />
                Created By: {offer.creator_id}
              </p>

              <div className="flex gap-4 text-sm mt-1">
                <p className="flex items-center gap-1">
                  <ClockIcon className="w-4 h-4" />
                  {new Date(offer.created_at).toLocaleString()}
                </p>

                {offer.completed_at && (
                  <p className="flex items-center gap-1">
                    <ClockIcon className="w-4 h-4" />
                    {new Date(offer.completed_at).toLocaleString()}
                  </p>
                )}
              </div>
            </div>
          ))}
        </div>
      )}

      <NavigationBar active="Trade" />
    </div>
  );
}
