import React, { useEffect, useState } from "react";
import axios from "axios";
import { io } from "socket.io-client";
import NavigationBar from "../components/NavigationBar";
import {
  UserIcon,
  ArrowRightOnRectangleIcon,
  BoltIcon,
} from "@heroicons/react/24/outline";

const Profile = () => {
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);

  const token = localStorage.getItem("token");

  // 1️⃣ Fetch user profile
  const fetchProfile = async () => {
    try {
      const res = await axios.get("http://localhost:5001/api/user/profile", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUserData(res.data.data);
      setLoading(false);
    } catch (error) {
      console.error("Profile fetch failed", error);
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchProfile();
  }, []);

  // 2️⃣ Real-time socket updates
  useEffect(() => {
    if (!token) return;

    const socket = io("http://localhost:5001", {
      auth: { token },
    });

    socket.on("connect", () => {
      console.log("Socket connected:", socket.id);
    });

    socket.on("new_reading", (data) => {
      console.log("Realtime update:", data);

      setUserData((prev) => ({
        ...prev,
        energy_balance: data.energy_balance,
        last_import_reading: data.last_import_reading,
        last_export_reading: data.last_export_reading,
      }));
    });

    socket.on("disconnect", () => {
      console.log("Socket disconnected");
    });

    return () => socket.disconnect();
  }, [token]);


  // 3️⃣ Logout
  const handleLogout = () => {
    localStorage.removeItem("token");
    window.location.reload();
  };


  if (loading || !userData)
    return (
      <div className="flex justify-center items-center h-screen">
        <p className="text-lg font-semibold">Loading...</p>
      </div>
    );


  return (
    <div className="min-h-screen bg-gray-100 flex flex-col">
      {/* Navigation */}
      <NavigationBar active="profile" />

      {/* Profile */}
      <div className="flex-grow p-6">
        <div className="max-w-xl mx-auto bg-white p-6 rounded-2xl shadow-md">

          <h2 className="text-2xl font-bold mb-4 flex items-center space-x-2">
            <UserIcon className="w-7 h-7 text-blue-600" />
            <span>Profile</span>
          </h2>

          {/* User Info */}
          <div className="space-y-3 mb-6">
            <p><span className="font-semibold">Name:</span> {userData.name}</p>
            <p><span className="font-semibold">Email:</span> {userData.email}</p>
            <p><span className="font-semibold">User ID:</span> {userData.user_id}</p>
            <p><span className="font-semibold">Meter ID:</span> {userData.meter_id}</p>
          </div>

          {/* Energy Info */}
          <div className="bg-gray-50 p-4 rounded-xl shadow-inner">
            <h3 className="text-lg font-semibold mb-3 flex items-center space-x-2">
              <BoltIcon className="w-6 h-6 text-yellow-500" />
              <span>Energy Balance</span>
            </h3>

            <p className="text-xl font-bold text-green-600">
              {userData.energy_balance} kWh
            </p>

            <div className="mt-3 space-y-2">
              <p>
                <span className="font-semibold">Last Import:</span>{" "}
                {userData.last_import_reading} kWh
              </p>
              <p>
                <span className="font-semibold">Last Export:</span>{" "}
                {userData.last_export_reading} kWh
              </p>
            </div>
          </div>

          {/* Logout */}
          <button
            onClick={handleLogout}
            className="mt-6 w-full flex items-center justify-center space-x-2 bg-red-500 text-white py-2 rounded-lg hover:bg-red-600 shadow-md"
          >
            <ArrowRightOnRectangleIcon className="w-5 h-5" />
            <span>Logout</span>
          </button>

        </div>
      </div>
    </div>
  );
};

export default Profile;
