import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { WalletIcon, CubeIcon, BoltIcon, UserIcon, CogIcon, KeyIcon } from "@heroicons/react/24/outline";

const NavigationBar = ({ active }) => {
  const navigate = useNavigate();
  const NavItem = ({ name, icon, route }) => (
    <button
      aria-label={name}
      className={`flex flex-col items-center justify-center p-2 text-xs font-medium transition-colors ${
        active === name ? "text-emerald-600 border-t-2 border-emerald-600" : "text-gray-500 hover:text-emerald-500"
      }`}
      onClick={() => navigate(route)}
    >
      {icon}
      {name}
    </button>
  );

  return (
    <div className="fixed inset-x-0 bottom-0 bg-white border-t border-gray-200 shadow-2xl z-50">
      <div className="max-w-4xl mx-auto flex justify-around">
        <NavItem name="Dashboard" icon={<BoltIcon className="w-5 h-5" />} route="/dashboard" />
        <NavItem name="Trade" icon={<CubeIcon className="w-5 h-5" />} route="/trade" />
        <NavItem name="History" icon={<WalletIcon className="w-5 h-5" />} route="/history" />
        <NavItem name="Profile" icon={<UserIcon className="w-5 h-5" />} route="/profile" />
      </div>
    </div>
  );
};

// Skeleton Loader
const Skeleton = ({ className }) => <div className={`bg-gray-200 animate-pulse rounded-xl ${className}`} />;

export default function Profile() {
  const navigate = useNavigate();
  const [userData, setUserData] = useState(null);
  const [errors, setErrors] = useState({ profile: "", balances: "", technical: "" });
  const [loading, setLoading] = useState(true);

  const [passwordMsg, setPasswordMsg] = useState("");
  const [currentPassword, setCurrentPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");

  const token = localStorage.getItem("token");

  useEffect(() => {
    if (!token) navigate("/login");
  }, [token, navigate]);

  const fetchProfile = async () => {
    setLoading(true);
    try {
      const res = await fetch("http://localhost:5001/api/auth/profile", {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      if (res.ok && data.success) setUserData(data.data);
      else setErrors({ profile: data.message || "Failed to fetch profile" });
    } catch (err) {
      console.error(err);
      setErrors({ profile: "Failed to fetch profile" });
    }
    setLoading(false);
  };

  useEffect(() => {
    fetchProfile();
  }, []);

  const handleChangePassword = async () => {
    if (!currentPassword || !newPassword) {
      setPasswordMsg("Please fill both fields.");
      return;
    }
    setPasswordMsg("Processing...");
    try {
      const res = await fetch("http://localhost:5001/api/auth/change-password", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ currentPassword, newPassword }),
      });
      const data = await res.json();
      setPasswordMsg(data.message || "Failed to update password");
      setCurrentPassword("");
      setNewPassword("");
    } catch {
      setPasswordMsg("Network error. Try again.");
    }
  };

  return (
    <div className="p-6 pb-20 max-w-4xl mx-auto space-y-6 bg-gray-50 min-h-screen">
      <h1 className="text-3xl font-bold text-gray-800 border-b pb-2 mb-4">Profile</h1>

      {/* Personal Info */}
      <div className="bg-gradient-to-r from-emerald-600 to-green-700 text-white p-6 rounded-xl shadow-lg space-y-2">
        <h2 className="text-xl font-bold flex items-center space-x-2">
          <UserIcon className="w-6 h-6" />
          <span>Personal & Identity</span>
        </h2>
        {loading ? (
          <Skeleton className="h-6 w-1/3" />
        ) : errors.profile ? (
          <p className="text-red-200 mt-1">{errors.profile}</p>
        ) : (
          <>
            <p><strong>Name:</strong> {userData?.name}</p>
            <p><strong>Email:</strong> {userData?.email}</p>
            <p><strong>User ID:</strong> {userData?.user_id}</p>
          </>
        )}
      </div>

      {/* Balances */}
      <h2 className="text-xl font-bold text-gray-800 pt-2">Balances</h2>
      {loading ? (
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <Skeleton className="h-24" />
          <Skeleton className="h-24" />
          <Skeleton className="h-24" />
        </div>
      ) : errors.profile ? (
        <p className="text-red-600">Failed to load balances</p>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div className="bg-white p-6 rounded-xl shadow border-l-4 border-indigo-500">
            <div className="flex items-center space-x-2 mb-2">
              <WalletIcon className="w-6 h-6 text-indigo-500" />
              <h3 className="font-semibold text-lg text-gray-700">Wallet Balance</h3>
            </div>
            <p className="text-2xl font-extrabold text-indigo-700">{userData?.wallet_balance}</p>
          </div>
          <div className="bg-white p-6 rounded-xl shadow border-l-4 border-teal-500">
            <div className="flex items-center space-x-2 mb-2">
              <CubeIcon className="w-6 h-6 text-teal-500" />
              <h3 className="font-semibold text-lg text-gray-700">Token Balance</h3>
            </div>
            <p className="text-2xl font-extrabold text-teal-700">{userData?.token_balance}</p>
          </div>
          <div className="bg-white p-6 rounded-xl shadow border-l-4 border-amber-500">
            <div className="flex items-center space-x-2 mb-2">
              <BoltIcon className="w-6 h-6 text-amber-500" />
              <h3 className="font-semibold text-lg text-gray-700">Energy Balance</h3>
            </div>
            <p className="text-2xl font-extrabold text-amber-700">{userData?.energy_balance}</p>
          </div>
        </div>
      )}

      {/* Technical Info */}
      <div className="bg-white p-6 rounded-xl shadow">
        <div className="flex items-center space-x-2 mb-4 border-b pb-2">
          <CogIcon className="w-6 h-6 text-gray-700" />
          <h3 className="font-semibold text-xl text-gray-800">Technical Info</h3>
        </div>
        {loading ? (
          <Skeleton className="h-24" />
        ) : errors.profile ? (
          <p className="text-red-600">Failed to load technical info</p>
        ) : (
          <>
            <p><strong>Meter ID:</strong> {userData?.meterId}</p>
            <p><strong>Transformer ID:</strong> {userData?.transformerId}</p>
            <p><strong>Total Energy Sold:</strong> {userData?.total_energy_sold}</p>
            <p><strong>Total Energy Bought:</strong> {userData?.total_energy_bought}</p>
          </>
        )}
      </div>

      {/* Change Password */}
      <div className="bg-white p-6 rounded-xl shadow">
        <div className="flex items-center space-x-2 mb-4 border-b pb-2">
          <KeyIcon className="w-6 h-6 text-red-500" />
          <h3 className="font-semibold text-xl text-gray-800">Change Password</h3>
        </div>
        {passwordMsg && (
          <p className={`text-sm mb-2 ${passwordMsg.toLowerCase().includes("success") ? "text-green-600" : "text-red-600"}`}>
            {passwordMsg}
          </p>
        )}
        <input type="password" placeholder="Current Password" className="border p-3 w-full rounded mb-3" value={currentPassword} onChange={(e) => setCurrentPassword(e.target.value)} />
        <input type="password" placeholder="New Password" className="border p-3 w-full rounded mb-4" value={newPassword} onChange={(e) => setNewPassword(e.target.value)} />
        <button className="bg-emerald-600 text-white p-3 rounded w-full hover:bg-emerald-700 transition" onClick={handleChangePassword}>
          {passwordMsg === "Processing..." ? "Updating..." : "Change Password"}
        </button>
      </div>

      <NavigationBar active="Profile" />
    </div>
  );
}
