import React, { useEffect, useState } from "react";
// REMOVED: import { useNavigate } from "react-router-dom";
import { WalletIcon, CubeIcon, BoltIcon, UserIcon, CogIcon, KeyIcon, ArrowRightOnRectangleIcon } from "@heroicons/react/24/outline";

// NavigationBar modified to use console.log instead of navigate
const NavigationBar = ({ active }) => {
  const NavItem = ({ name, icon, route }) => (
    <button
      aria-label={name}
      className={`flex flex-col items-center justify-center p-2 text-xs font-medium transition-colors ${
        active === name ? "text-emerald-600 border-t-2 border-emerald-600" : "text-gray-500 hover:text-emerald-500"
      }`}
      // Replaced navigation with console log
      onClick={() => console.log(`Simulating navigation to ${route}`)}
    >
      {icon}
      {name}
    </button>
  );

  return (
    <div className="fixed inset-x-0 bottom-0 bg-white border-t border-gray-200 shadow-2xl z-50">
      <div className="max-w-4xl mx-auto flex justify-around">
        <NavItem name="Dashboard" icon={<BoltIcon className="w-5 h-5" />} route="/dashboard" />
        <NavItem name="Trade" icon={<CubeIcon className="w-5 h-5" />} route="/trade" />
        <NavItem name="History" icon={<WalletIcon className="w-5 h-5" />} route="/history" />
        <NavItem name="Profile" icon={<UserIcon className="w-5 h-5" />} route="/profile" />
      </div>
    </div>
  );
};

const Skeleton = ({ className }) => <div className={`bg-gray-200 animate-pulse rounded-xl ${className}`} />;

// Renamed Profile to App (main exported component)
export default function App() {
  // State to simulate authentication
  const [isAuthenticated, setIsAuthenticated] = useState(!!localStorage.getItem("token"));
  const [userData, setUserData] = useState(null);
  const [errors, setErrors] = useState({ profile: "", balances: "", technical: "" });
  const [loading, setLoading] = useState(false); // Set to false initially until we check token

  const [showPasswordFields, setShowPasswordFields] = useState(false);
  const [passwordMsg, setPasswordMsg] = useState("");
  const [currentPassword, setCurrentPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");

  const token = localStorage.getItem("token");

  // MOCK FETCH FUNCTION (since external API is not available)
  const mockFetch = async (endpoint, options = {}) => {
    await new Promise(resolve => setTimeout(resolve, 800)); // Simulate network latency

    if (!token) throw new Error("Unauthorized");

    let data;
    if (endpoint.includes("profile")) {
      data = { success: true, data: { 
        name: "A. G. Customer", 
        email: "a.g.customer@grid.com", 
        _id: "NODE-X87F32",
        wallet_balance: "$1,250.00",
        token_balance: "5,800 P2P",
        energy_balance: "450 kWh",
        reserved_token_balance: "1,200 P2P",
        reserved_energy_balance: "150 kWh",
        meterId: "MT-3029-A", 
        transformerId: "TX-445-Z",
        total_energy_sold: "1,560 kWh", 
        total_energy_bought: "980 kWh"
      } };
    } else if (endpoint.includes("change-password")) {
      const body = JSON.parse(options.body);
      if (body.currentPassword === "wrong_key" || body.newPassword.length < 6) {
        return { json: () => ({ success: false, message: "Password must be at least 6 characters and current key must be correct." }) };
      }
      return { json: () => ({ success: true, message: "Secure Key updated successfully!" }) };
    } else {
      data = { success: false, message: "Not Found" };
    }
    
    return { 
      ok: true, 
      json: () => data 
    };
  };

  const fetchProfile = async () => {
    setLoading(true);
    try {
      // Using mockFetch to simulate API call
      const res = await mockFetch("/api/auth/profile", {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      if (res.ok && data.success) {
        setUserData(data.data);
        setErrors({});
      }
      else setErrors({ profile: data.message || "Failed to fetch profile" });
    } catch (err) {
      console.error("Fetch Error:", err);
      setErrors({ profile: "Failed to fetch profile" });
    }
    setLoading(false);
  };

  // Initial authentication check and data fetch
  useEffect(() => {
    const currentToken = localStorage.getItem("token");
    if (currentToken) {
      setIsAuthenticated(true);
      fetchProfile();
    } else {
      setIsAuthenticated(false);
    }
  }, []); 

  const handleChangePassword = async () => {
    if (!currentPassword || !newPassword) {
      setPasswordMsg("Please fill both fields.");
      return;
    }
    setPasswordMsg("Processing...");
    try {
      // Using mockFetch to simulate API call
      const res = await mockFetch("/api/auth/change-password", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ currentPassword, newPassword }),
      });
      const data = await res.json();
      setPasswordMsg(data.message || "Network error. Try again.");
      if (data.success) {
        setCurrentPassword("");
        setNewPassword("");
        setShowPasswordFields(false);
      }
    } catch {
      setPasswordMsg("Network error. Try again.");
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    // Change state instead of navigating
    setIsAuthenticated(false); 
    setUserData(null);
  };

  // --- RENDERING LOGIC ---
  if (!isAuthenticated) {
    // Simulated Login View
    return (
        <div className="min-h-screen flex items-center justify-center bg-gray-100 p-4">
            <div className="w-full max-w-md bg-white p-8 md:p-10 rounded-2xl shadow-2xl border-t-8 border-red-500 text-center">
                <h2 className="text-2xl font-bold text-gray-800 mb-4">Session Expired / Logged Out</h2>
                <p className="text-gray-600 mb-6">Please simulate logging in again to view the Profile.</p>
                <button 
                  onClick={() => {
                      // Simulating successful login by setting a token
                      localStorage.setItem("token", "simulated-token-" + Date.now());
                      setIsAuthenticated(true);
                  }}
                  className="w-full py-3 px-4 rounded-xl bg-emerald-600 text-white font-semibold transition duration-200 shadow-md hover:bg-emerald-700"
                >
                  Simulate Login
                </button>
            </div>
        </div>
    );
  }

  if (loading) return <div className="p-6 text-center text-xl font-semibold text-gray-700 min-h-screen bg-gray-50 flex items-center justify-center">Loading P2P Node Data...</div>;

  return (
    <div className="p-6 pb-24 max-w-4xl mx-auto space-y-6 bg-gray-50 min-h-screen">
      <div className="flex justify-between items-center mb-4">
        <h1 className="text-3xl font-bold text-gray-800 border-b pb-2">Profile</h1>
        <button
          onClick={handleLogout}
          className="flex items-center space-x-1 bg-red-500 text-white px-4 py-2 rounded-lg hover:bg-red-600 transition shadow-md"
        >
          <ArrowRightOnRectangleIcon className="w-5 h-5" />
          <span>Logout</span>
        </button>
      </div>

      {/* Personal Info */}
      <div className="bg-gradient-to-r from-emerald-600 to-green-700 text-white p-6 rounded-xl shadow-lg space-y-2">
        <h2 className="text-xl font-bold flex items-center space-x-2">
          <UserIcon className="w-6 h-6" />
          <span>Personal & Identity</span>
        </h2>
        {errors.profile ? (
          <p className="text-red-200 mt-1">{errors.profile}</p>
        ) : (
          <>
            <p className="text-sm"><strong>Name:</strong> {userData?.name || 'N/A'}</p>
            <p className="text-sm"><strong>Email:</strong> {userData?.email || 'N/A'}</p>
            <p className="text-sm">
                <strong>User ID:</strong> 
                <span className="font-mono bg-white bg-opacity-20 p-1 rounded ml-1 text-xs">{userData?._id || 'N/A'}</span>
            </p>
          </>
        )}
      </div>

      {/* Balances */}
      <h2 className="text-xl font-bold text-gray-800 pt-2">Current Balances</h2>
      {errors.profile ? (
        <p className="text-red-600">Failed to load balances</p>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div className="bg-white p-6 rounded-xl shadow border-l-4 border-indigo-500">
            <div className="flex items-center space-x-2 mb-2">
              <WalletIcon className="w-6 h-6 text-indigo-500" />
              <h3 className="font-semibold text-lg text-gray-700">Wallet Balance</h3>
            </div>
            <p className="text-2xl font-extrabold text-indigo-700">{userData?.wallet_balance || '-'}</p>
          </div>
          <div className="bg-white p-6 rounded-xl shadow border-l-4 border-teal-500">
            <div className="flex items-center space-x-2 mb-2">
              <CubeIcon className="w-6 h-6 text-teal-500" />
              <h3 className="font-semibold text-lg text-gray-700">Token Balance</h3>
            </div>
            <p className="text-2xl font-extrabold text-teal-700">{userData?.token_balance || '-'}</p>
          </div>
          <div className="bg-white p-6 rounded-xl shadow border-l-4 border-amber-500">
            <div className="flex items-center space-x-2 mb-2">
              <BoltIcon className="w-6 h-6 text-amber-500" />
              <h3 className="font-semibold text-lg text-gray-700">Energy Balance</h3>
            </div>
            <p className="text-2xl font-extrabold text-amber-700">{userData?.energy_balance || '-'}</p>
          </div>
        </div>
      )}

      {/* Reserved Balances */}
      <h2 className="text-xl font-bold text-gray-800 pt-2">Reserved Balances (In Trade)</h2>
      {errors.profile ? (
        <p className="text-red-600">Failed to load reserved balances</p>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div className="bg-white p-6 rounded-xl shadow border-l-4 border-teal-400">
            <h3 className="font-semibold text-gray-700">Reserved Token Balance</h3>
            <p className="text-xl font-bold text-teal-600">{userData?.reserved_token_balance || '-'}</p>
          </div>
          <div className="bg-white p-6 rounded-xl shadow border-l-4 border-amber-400">
            <h3 className="font-semibold text-gray-700">Reserved Energy Balance</h3>
            <p className="text-xl font-bold text-amber-600">{userData?.reserved_energy_balance || '-'}</p>
          </div>
        </div>
      )}

      {/* Technical Info */}
      <div className="bg-white p-6 rounded-xl shadow mt-4">
        <div className="flex items-center space-x-2 mb-4 border-b pb-2">
          <CogIcon className="w-6 h-6 text-gray-700" />
          <h3 className="font-semibold text-xl text-gray-800">Technical Info & History</h3>
        </div>
        {errors.profile ? (
          <p className="text-red-600">Failed to load technical info</p>
        ) : (
          <div className="grid grid-cols-2 gap-4 text-gray-700">
            <div className="space-y-2">
              <p><strong>Meter ID:</strong> <span className="font-mono bg-gray-100 p-1 rounded text-sm">{userData?.meterId || 'N/A'}</span></p>
              <p><strong>Transformer ID:</strong> <span className="font-mono bg-gray-100 p-1 rounded text-sm">{userData?.transformerId || 'N/A'}</span></p>
            </div>
            <div className="space-y-2">
              <p><strong>Total Energy Sold:</strong> <span className="font-bold text-emerald-600">{userData?.total_energy_sold || '0 kWh'}</span></p>
              <p><strong>Total Energy Bought:</strong> <span className="font-bold text-indigo-600">{userData?.total_energy_bought || '0 kWh'}</span></p>
            </div>
          </div>
        )}
      </div>

      {/* Change Password */}
      <div className="bg-white p-6 rounded-xl shadow">
        <div className="flex justify-between items-center mb-4 border-b pb-2">
          <div className="flex items-center space-x-2">
            <KeyIcon className="w-6 h-6 text-red-500" />
            <h3 className="font-semibold text-xl text-gray-800">Change Secure Key</h3>
          </div>
          <button
            onClick={() => setShowPasswordFields(!showPasswordFields)}
            className="text-emerald-600 hover:text-emerald-800 font-semibold transition p-1 rounded-lg"
          >
            {showPasswordFields ? "Cancel" : "Update"}
          </button>
        </div>

        {showPasswordFields && (
          <>
            {passwordMsg && (
              <p className={`text-sm mb-2 ${passwordMsg.toLowerCase().includes("success") ? "text-green-600" : "text-red-600"}`}>
                {passwordMsg}
              </p>
            )}
            <input 
                type="password" 
                placeholder="Current Secure Key (Password)" 
                className="border p-3 w-full rounded-lg mb-3 focus:outline-none focus:ring-2 focus:ring-emerald-400 transition" 
                value={currentPassword} 
                onChange={(e) => setCurrentPassword(e.target.value)} 
            />
            <input 
                type="password" 
                placeholder="New Secure Key (Password)" 
                className="border p-3 w-full rounded-lg mb-4 focus:outline-none focus:ring-2 focus:ring-emerald-400 transition" 
                value={newPassword} 
                onChange={(e) => setNewPassword(e.target.value)} 
            />
            <button 
                className="bg-emerald-600 text-white p-3 rounded-lg w-full hover:bg-emerald-700 transition font-semibold shadow-md" 
                onClick={handleChangePassword}
            >
              {passwordMsg === "Processing..." ? "Updating..." : "Change Secure Key"}
            </button>
          </>
        )}
      </div>

      <NavigationBar active="Profile" />
    </div>
  );
}
