import React, { useState, useEffect } from "react";
import axios from "axios";
import { BoltIcon, ClockIcon, UserIcon } from "@heroicons/react/24/outline";
import NavigationBar from "../components/navbar.jsx"; // navigation

export default function TradePage() {
  const [offers, setOffers] = useState([]);
  const [newOffer, setNewOffer] = useState({
    offer_type: "sell",
    units: 0,
    token_per_unit: 0,
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  const API = "http://localhost:5001";

  // Auth header helper
  const authHeader = () => {
    const token = localStorage.getItem("token");
    return {
      "Content-Type": "application/json",
      Authorization: `Bearer ${token}`,
    };
  };

  // Fetch current offers
  const fetchOffers = async () => {
    try {
      setLoading(true);
      const res = await axios.get(`${API}/api/offers/current`, {
        headers: authHeader(),
      });
      setOffers(res.data.data || []);
      setError("");
    } catch (err) {
      console.error(err);
      setError("Failed to fetch offers");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchOffers();
  }, []);

  // Handle creating a new offer
  const handleCreateOffer = async (e) => {
    e.preventDefault();

    const creator_id = localStorage.getItem("user_id");
    if (!creator_id) {
      setError("User not logged in");
      return;
    }

    const offerBody = {
      creator_id,
      offer_type: newOffer.offer_type,
      units: Number(newOffer.units),
      token_per_unit: Number(newOffer.token_per_unit),
    };

    try {
      const res = await axios.post(`${API}/api/offers/create`, offerBody, {
        headers: authHeader(),
      });

      if (res.data.success) {
        fetchOffers(); // refresh
        setNewOffer({ offer_type: "sell", units: 0, token_per_unit: 0 });
        setError("");
      } else {
        setError(res.data.message || "Failed to create offer");
      }
    } catch (err) {
      console.error(err);
      setError("Failed to create offer");
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-green-600 to-green-800 text-white p-6 pb-24">
      <h1 className="text-3xl font-bold text-center mb-6">⚡ Energy Trading</h1>

      {/* Error message */}
      {error && (
        <div className="mb-4 text-center text-red-300 font-semibold">
          ⚠️ {error}
        </div>
      )}

      {/* New Offer Form */}
      <div className="bg-green-700 rounded-lg p-4 mb-6 shadow-md">
        <h2 className="font-semibold mb-2">Create New Offer</h2>
        <form
          className="flex flex-wrap gap-4 items-end"
          onSubmit={handleCreateOffer}
        >
          <div>
            <label className="block mb-1">Type</label>
            <select
              value={newOffer.offer_type}
              onChange={(e) =>
                setNewOffer({ ...newOffer, offer_type: e.target.value })
              }
              className="text-black rounded px-2 py-1"
            >
              <option value="sell">Sell</option>
              <option value="buy">Buy</option>
            </select>
          </div>

          <div>
            <label className="block mb-1">Units</label>
            <input
              type="number"
              value={newOffer.units}
              onChange={(e) =>
                setNewOffer({ ...newOffer, units: Number(e.target.value) })
              }
              className="text-black rounded px-2 py-1"
              min={1}
              required
            />
          </div>

          <div>
            <label className="block mb-1">Token/Unit</label>
            <input
              type="number"
              value={newOffer.token_per_unit}
              onChange={(e) =>
                setNewOffer({
                  ...newOffer,
                  token_per_unit: Number(e.target.value),
                })
              }
              className="text-black rounded px-2 py-1"
              min={1}
              required
            />
          </div>

          <button
            type="submit"
            disabled={newOffer.units <= 0 || newOffer.token_per_unit <= 0}
            className="bg-yellow-500 hover:bg-yellow-600 text-black px-4 py-2 rounded disabled:opacity-50"
          >
            Create
          </button>
        </form>
      </div>

      {/* Offers List */}
      {loading ? (
        <div className="text-center text-lg">⏳ Loading offers...</div>
      ) : offers.length === 0 ? (
        <div className="text-center text-lg">No active offers found.</div>
      ) : (
        <div className="grid gap-4">
          {offers.map((offer) => (
            <div
              key={offer._id}
              className={`p-4 rounded-lg shadow-md ${
                offer.status === "cancelled"
                  ? "bg-red-500 text-white"
                  : "bg-green-500 hover:bg-green-600 text-white"
              }`}
            >
              <div className="flex justify-between items-center mb-2">
                <h3 className="font-bold text-lg flex items-center gap-2">
                  <BoltIcon className="w-5 h-5" /> {offer.offer_id}
                </h3>
                <span className="px-2 py-1 rounded-full bg-red-700 text-sm">
                  {offer.status}
                </span>
              </div>

              <p>
                <strong>Type:</strong> {offer.offer_type}
              </p>
              <p>
                <strong>Units:</strong> {offer.units} kWh
              </p>
              <p>
                <strong>Rate/Unit:</strong> {offer.token_per_unit} Tokens
              </p>
              <p>
                <strong>Total Tokens:</strong>{" "}
                {offer.total_tokens ?? offer.units * offer.token_per_unit}
              </p>
              <p className="flex items-center gap-1">
                <UserIcon className="w-4 h-4" /> Created By: {offer.creator_id}
              </p>
              <div className="flex gap-4 mt-2 text-sm">
                <p className="flex items-center gap-1">
                  <ClockIcon className="w-4 h-4" />{" "}
                  {new Date(offer.created_at).toLocaleString()}
                </p>
                {offer.completed_at && (
                  <p className="flex items-center gap-1">
                    <ClockIcon className="w-4 h-4" />{" "}
                    {new Date(offer.completed_at).toLocaleString()}
                  </p>
                )}
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Navigation Bar */}
      <NavigationBar active="Trade" />
    </div>
  );
}
